import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(null);
  const [view, setView] = useState('login');
  const [learningPath, setLearningPath] = useState(null);
  const [progress, setProgress] = useState({
    completedModules: [],
    currentModule: 0,
    overallProgress: 0
  });

  useEffect(() => {
    console.log('Checking stored token...');
    console.log('Stored user:', localStorage.getItem('currentUser'));

    const storedUser = localStorage.getItem('currentUser');
    const storedToken = localStorage.getItem('token');

    if (storedUser && storedToken) {
      console.log('Found stored credentials, logging in...');
      setCurrentUser(storedUser);
      setToken(storedToken);
      setView('dashboard');
    } else {
      console.log('No stored credentials found');
    }
  }, []);

  useEffect(() => {
    if (currentUser && token) {
      loadUserData();
    }
  }, [currentUser, token]);

  const loadUserData = async () => {
    if (!currentUser || !token) {
      console.log('No user or token');
      return;
    }

    console.log('Loading data for user:', currentUser);

    // First, try to load from localStorage
    try {
      const storedPath = localStorage.getItem('learningPath');
      const storedProgress = localStorage.getItem('progress');

      if (storedPath) {
        const path = JSON.parse(storedPath);
        console.log('✅ Loaded learning path from localStorage');
        setLearningPath(path);
      }

      if (storedProgress) {
        const progress = JSON.parse(storedProgress);
        console.log('✅ Loaded progress from localStorage');
        setProgress(progress);
      }

      // If we found data in localStorage, we're done
      if (storedPath && storedProgress) {
        return;
      }
    } catch (err) {
      console.log('No data in localStorage, will try API');
    }

    // Fallback: Try to fetch from API
    try {
      const progressResponse = await axios.get(
        `${API_URL}/api/progress/user/${currentUser}`,
        { headers: { Authorization: `Bearer ${token}` } }
      ).catch(err => {
        console.log('Could not fetch progress from API');
        return null;
      });

      if (progressResponse && progressResponse.data) {
        console.log('✅ Fetched progress from API');
        setProgress(progressResponse.data);
        localStorage.setItem('progress', JSON.stringify(progressResponse.data));
      }
    } catch (err) {
      console.log('API fetch failed (expected if no data exists yet)');
    }
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/auth/login`, {
        username,
        password
      });

      console.log('Login successful for user:', username);

      setCurrentUser(username);
      setToken(response.data.token);
      localStorage.setItem('currentUser', username);
      localStorage.setItem('token', response.data.token);
      setView('dashboard');

      return { success: true };
    } catch (error) {
      console.error('Login failed:', error);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Login failed' 
      };
    }
  };

  const handleRegister = async (username, password) => {
    try {
      await axios.post(`${API_URL}/api/auth/register`, {
        username,
        password
      });

      console.log('Registration successful for:', username);
      return { success: true };
    } catch (error) {
      console.error('Registration failed:', error);
      return { 
        success: false, 
        message: error.response?.data?.message || 'Registration failed' 
      };
    }
  };

  const handleLogout = () => {
    console.log('Logging out...');
    setCurrentUser(null);
    setToken(null);
    setLearningPath(null);
    setProgress({
      completedModules: [],
      currentModule: 0,
      overallProgress: 0
    });
    localStorage.removeItem('currentUser');
    localStorage.removeItem('token');
    localStorage.removeItem('learningPath');
    localStorage.removeItem('progress');
    setView('login');
  };

  const handlePathGenerated = (newPath, newProgress) => {
    console.log('New learning path generated');
    setLearningPath(newPath);

    if (newProgress) {
      setProgress(newProgress);
      localStorage.setItem('progress', JSON.stringify(newProgress));
    }

    if (newPath) {
      localStorage.setItem('learningPath', JSON.stringify(newPath));
    }

    setView('dashboard');
  };

  const handleProgressUpdate = async (updatedProgress) => {
    try {
      const response = await axios.post(
        `${API_URL}/api/progress/update`,
        updatedProgress,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('✅ Progress updated on server');
      setProgress(response.data);
      localStorage.setItem('progress', JSON.stringify(response.data));

      return { success: true };
    } catch (error) {
      console.error('Failed to update progress:', error);
      return { success: false, message: 'Failed to update progress' };
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Learning Path Generator</h1>
        {currentUser && (
          <div className="user-info">
            <span>Welcome, {currentUser}!</span>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>
        )}
      </header>

      <main>
        {!currentUser && view === 'login' && (
          <Login 
            onLogin={handleLogin}
            onSwitchToRegister={() => setView('register')}
          />
        )}

        {!currentUser && view === 'register' && (
          <Register 
            onRegister={handleRegister}
            onSwitchToLogin={() => setView('login')}
          />
        )}

        {currentUser && view === 'dashboard' && (
          <Dashboard
            learningPath={learningPath}
            progress={progress}
            onStartQuiz={() => setView('quiz')}
            onProgressUpdate={handleProgressUpdate}
          />
        )}

        {currentUser && view === 'quiz' && (
          <Quiz
            currentUser={currentUser}
            token={token}
            onBack={() => setView('dashboard')}
            onPathGenerated={handlePathGenerated}
          />
        )}
      </main>
    </div>
  );
}

export default App;
