import React, { useState, useCallback } from 'react';
import axios from 'axios';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(null);
  const [view, setView] = useState('login');
  const [learningPath, setLearningPath] = useState(null);
  const [progress, setProgress] = useState(null);

  // Load progress and learningPath for the logged-in user
  const loadUserData = useCallback(async (username, token) => {
    if (!username || !token) return;
    // Load progress from backend and save user-scoped
    try {
      const resp = await axios.get(
        `${API_URL}/api/progress/user/${username}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (resp.data) {
        setProgress(resp.data);
        localStorage.setItem(`progress_${username}`, JSON.stringify(resp.data));
      }
    } catch (err) {
      const storedProgress = localStorage.getItem(`progress_${username}`);
      setProgress(storedProgress ? JSON.parse(storedProgress) : null);
    }
    // Load learningPath from user-scoped localStorage
    const storedPath = localStorage.getItem(`learningPath_${username}`);
    setLearningPath(storedPath ? JSON.parse(storedPath) : null);
  }, []);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/auth/login`, { username, password });
      setCurrentUser(username);
      setToken(response.data.token);
      setView('dashboard');
      localStorage.setItem('currentUser', username);
      localStorage.setItem('token', response.data.token);
      await loadUserData(username, response.data.token);
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.message || 'Login failed' };
    }
  };

  const handleRegister = async (username, password) => {
    try {
      await axios.post(`${API_URL}/api/auth/register`, { username, password });
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.message || 'Registration failed' };
    }
  };

  const handleLogout = () => {
    if (currentUser) {
      localStorage.removeItem(`learningPath_${currentUser}`);
      localStorage.removeItem(`progress_${currentUser}`);
      localStorage.removeItem('currentUser');
      localStorage.removeItem('token');
    }
    setCurrentUser(null);
    setToken(null);
    setLearningPath(null);
    setProgress(null);
    setView('login');
  };

  const handlePathGenerated = (newPath, newProgress) => {
    setLearningPath(newPath);
    if (currentUser) {
      localStorage.setItem(`learningPath_${currentUser}`, JSON.stringify(newPath));
      if (newProgress) {
        setProgress(newProgress);
        localStorage.setItem(`progress_${currentUser}`, JSON.stringify(newProgress));
      }
    }
    setView('dashboard');
  };

  const handleProgressUpdate = async (updatedProgress) => {
    try {
      const response = await axios.post(
        `${API_URL}/api/progress/update`,
        updatedProgress,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProgress(response.data);
      if (currentUser) {
        localStorage.setItem(`progress_${currentUser}`, JSON.stringify(response.data));
      }
      return { success: true };
    } catch (error) {
      return { success: false, message: 'Failed to update progress' };
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Learning Path Generator</h1>
        {currentUser && (
          <div className="user-info">
            <span>Welcome, {currentUser}!</span>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>
        )}
      </header>
      <main>
        {view === 'login' && (
          <Login onLogin={handleLogin} onSwitchToRegister={() => setView('register')} />
        )}
        {view === 'register' && (
          <Register onRegister={handleRegister} onSwitchToLogin={() => setView('login')} />
        )}
        {currentUser && view === 'dashboard' && (
          <Dashboard
            learningPath={learningPath}
            progress={progress}
            onStartQuiz={() => setView('quiz')}
            onProgressUpdate={handleProgressUpdate}
          />
        )}
        {currentUser && view === 'quiz' && (
          <Quiz
            currentUser={currentUser}
            token={token}
            onBack={() => setView('dashboard')}
            onPathGenerated={handlePathGenerated}
          />
        )}
      </main>
    </div>
  );
}

export default App;
