import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Quiz.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function Quiz({ currentUser, token, onBack, onPathGenerated }) {
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);
  const [weeklyHours, setWeeklyHours] = useState(10);
  const [target, setTarget] = useState('');

  useEffect(() => {
    loadTopics();
  }, []);

  const loadTopics = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/quiz/topics`);
      setTopics(Array.from(response.data));
    } catch (error) {
      console.error('Error loading topics:', error);
    }
  };

  const loadQuiz = async (topicId) => {
    try {
      const response = await axios.get(`${API_URL}/api/quiz/${topicId}`);
      setQuiz(response.data);
      setSelectedTopic(topicId);
      setAnswers({});
      setResult(null);
    } catch (error) {
      console.error('Error loading quiz:', error);
    }
  };

  const handleAnswerChange = (questionId, answerIndex) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: answerIndex
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (Object.keys(answers).length < quiz.questions.length) {
      alert('Please answer all questions');
      return;
    }

    try {
      const payload = {
        answers,
        weeklyHours,
        name: currentUser,
        target
      };

      const response = await axios.post(
        `${API_URL}/api/quiz/${selectedTopic}/submit`,
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setResult(response.data);

      // USER SCOPED LOCALSTORAGE
      if (response.data.learningPath && currentUser) {
        localStorage.setItem(`learningPath_${currentUser}`, JSON.stringify(response.data.learningPath));
      }
      if (response.data.progress && currentUser) {
        localStorage.setItem(`progress_${currentUser}`, JSON.stringify(response.data.progress));
      }

      if (onPathGenerated) {
        onPathGenerated(response.data.learningPath, response.data.progress);
      }
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Error submitting quiz. Please try again.');
    }
  };

  const handleViewDashboard = () => {
    if (result && result.learningPath && onPathGenerated) {
      onPathGenerated(result.learningPath, result.progress);
    }
  };

  return (
    <div className="quiz-container">
      <button onClick={onBack} className="back-button">← Back to Dashboard</button>

      {!selectedTopic && (
        <div className="topic-selection">
          <h2>Select a Topic</h2>
          <div className="topic-grid">
            {topics.map(topic => (
              <div key={topic} className="topic-card" onClick={() => loadQuiz(topic)}>
                <h3>{topic.replace('t-', '').replace(/-/g, ' ').toUpperCase()}</h3>
                <p>Take this quiz to assess your skills</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {quiz && !result && (
        <div className="quiz-form">
          <h2>{quiz.topicName} Quiz</h2>
          <p>{quiz.description}</p>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <label>Weekly Study Hours:</label>
              <input
                type="number"
                min="1"
                max="40"
                value={weeklyHours}
                onChange={(e) => setWeeklyHours(parseInt(e.target.value))}
              />
            </div>
            <div className="input-group">
              <label>Career Target (e.g., Backend Developer):</label>
              <input
                type="text"
                value={target}
                onChange={(e) => setTarget(e.target.value)}
                placeholder="Enter your career goal"
              />
            </div>
            {quiz.questions.map((question, qIndex) => (
              <div key={question.id} className="question">
                <h3>{qIndex + 1}. {question.text}</h3>
                {question.options.map((option, oIndex) => (
                  <label key={oIndex} className="option">
                    <input
                      type="radio"
                      name={question.id}
                      checked={answers[question.id] === oIndex}
                      onChange={() => handleAnswerChange(question.id, oIndex)}
                    />
                    {option}
                  </label>
                ))}
              </div>
            ))}
            <button type="submit" className="submit-button">Submit Quiz</button>
          </form>
        </div>
      )}

      {result && (
        <div className="result-container">
          <h2>Quiz Results</h2>
          <div className="result-card">
            <p>Score: {result.correct}/{result.total} ({Math.round(result.score * 100)}%)</p>
            <p>Skill Level: <strong>{result.inferredSkill}</strong></p>
          </div>
          {result.learningPath && (
            <div className="learning-path-preview">
              <h3>Your Learning Path: {result.learningPath.title}</h3>
              <p>{result.learningPath.description}</p>
              <p>Estimated Duration: {result.learningPath.estimatedWeeks} weeks</p>
              <p>Total Modules: {result.learningPath.modules?.length || 0}</p>
              <button onClick={handleViewDashboard} className="view-dashboard-button">
                View Full Learning Path →
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Quiz;
