import React, { useState } from 'react';
import './Login.css';

function Login({ onLogin, onSwitchToRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg('');
    if (!username || !password) {
      setErrorMsg('Please enter both username and password.');
      return;
    }
    setLoading(true);
    const result = await onLogin(username, password);
    setLoading(false);
    if (!result.success) {
      setErrorMsg(result.message || 'Login failed.');
    }
  };

  return (
    <div className="login-page-bg">
      <form className="login-form-card bounce-in" onSubmit={handleSubmit} autoComplete="off">
        <h2 className="login-title">ðŸš€ Sign In</h2>
        <div className="input-group">
          <label htmlFor="login-username">Username</label>
          <input
            id="login-username"
            type="text"
            value={username}
            onChange={e => setUsername(e.target.value)}
            autoFocus
            className="login-input"
            placeholder="Enter your username"
            autoComplete="username"
            disabled={loading}
          />
        </div>
        <div className="input-group password-group">
          <label htmlFor="login-password">Password</label>
          <input
            id="login-password"
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={e => setPassword(e.target.value)}
            className="login-input"
            placeholder="Enter your password"
            autoComplete="current-password"
            disabled={loading}
          />
          <span
            className="toggle-password"
            onClick={() => setShowPassword(v => !v)}
            title={showPassword ? "Hide Password" : "Show Password"}
            tabIndex={0}
            role="button"
            aria-label="Toggle password visibility"
          >
            {showPassword ? (
              <svg height="22" viewBox="0 0 24 24">
                <path fill="#667eea" d="M12 4.5C7.305 4.5 3.135 7.36 1 12c2.135 4.64 6.305 7.5 11 7.5s8.865-2.86 11-7.5C20.865 7.36 16.695 4.5 12 4.5zm0 12c-2.485 0-4.5-2.015-4.5-4.5S9.515 7.5 12 7.5s4.5 2.015 4.5 4.5-2.015 4.5-4.5 4.5z"/><circle fill="#667eea" cx="12" cy="12" r="2"/>
              </svg>
            ) : (
              <svg height="22" viewBox="0 0 24 24">
                <path fill="#667eea" d="M12 6.5c-2.485 0-4.5 2.015-4.5 4.5 0 .785.233 1.51.626 2.116l-1.299 1.3C6.52 13.37 6.5 12.69 6.5 12c0-4.64 4.17-7.5 9.5-7.5.296 0 .588.03.876.086l-1.299 1.3A4.49 4.49 0 0 0 12 6.5zM2.91 2.91l18.18 18.18-1.41 1.41-2.2-2.2A11.95 11.95 0 0 1 12 19.5c-4.695 0-8.865-2.86-11-7.5a11.94 11.94 0 0 1 3.69-4.73L1.5 4.32l1.41-1.41zm7.09 7.09l2.12 2.12A2.49 2.49 0 0 1 12 14.5c-1.379 0-2.5-1.121-2.5-2.5a2.48 2.48 0 0 1 1.5-2.31z"/>
              </svg>
            )}
          </span>
        </div>
        {errorMsg && <div className="error-msg">{errorMsg}</div>}
        <button type="submit" className="login-btn creative-btn" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>
        <div className="switch-link">
          <span>Don't have an account?</span>
          <button
            type="button"
            className="creative-btn"
            onClick={onSwitchToRegister}
            disabled={loading}
          >
            Register
          </button>
        </div>
      </form>
    </div>
  );
}

export default Login;
