import React from 'react';
import './Dashboard.css';

function Dashboard({ learningPath, progress, onStartQuiz, onProgressUpdate }) {
  // Helper: export modules as CSV
  const handleExportCSV = () => {
    if (!learningPath || !learningPath.modules) return;
    const csvRows = [
      ['Module #', 'Title', 'Description', 'Estimated Hours', 'Topics']
    ];
    learningPath.modules.forEach((module, idx) => {
      csvRows.push([
        idx + 1,
        `"${module.title}"`,
        `"${module.description}"`,
        module.estimatedHours,
        `"${(module.topics || []).join('; ')}"`
      ]);
    });
    const csvContent = csvRows.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${learningPath.title.replace(/\s+/g, '_')}_modules.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  // Mark module as complete
  const handleCompleteModule = async (moduleIndex) => {
    if (!progress || !learningPath) return;
    const completedModules = progress.completedModules || [];
    if (completedModules.includes(moduleIndex)) {
      alert('Module already completed!');
      return;
    }
    const newCompletedModules = [...completedModules, moduleIndex];
    const newProgress = (newCompletedModules.length / progress.totalModules) * 100;
    const updatedProgress = {
      ...progress,
      completedModules: newCompletedModules,
      currentModule: moduleIndex + 1,
      overallProgress: newProgress
    };
    const result = await onProgressUpdate(updatedProgress);
    if (result.success) {
      showConfetti();
      alert('üéâ Module completed! Keep going!');
    } else {
      alert('Failed to update progress');
    }
  };

  // Fun confetti effect!
  const showConfetti = () => {
    const canvas = document.createElement('canvas');
    canvas.className = 'confetti-canvas';
    document.body.appendChild(canvas);
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const pieces = Array.from({ length: 80 }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height * 0.2,
      r: Math.random() * 8 + 4,
      c: `hsl(${Math.random()*360},100%,50%)`,
      s: Math.random() * 2 + 2
    }));

    let frame = 0;
    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      pieces.forEach(p => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, 2*Math.PI);
        ctx.fillStyle = p.c;
        ctx.fill();
        p.y += p.s;
        p.x += Math.sin(frame/10 + p.y/20) * 2;
      });
      frame++;
      if (frame < 60) requestAnimationFrame(draw);
      else document.body.removeChild(canvas);
    }
    draw();
  };

  if (!learningPath) {
    return (
      <div className="dashboard-container crazy">
        <div className="no-path-card crazy-card">
          <h2>üéì Welcome, Future Expert!</h2>
          <p>You haven't started a learning path yet.</p>
          <p>Take a quiz to unlock your journey!</p>
          <button onClick={onStartQuiz} className="start-quiz-btn crazy-btn">
            üöÄ Take a Quiz
          </button>
        </div>
      </div>
    );
  }

  const completedCount = progress?.completedModules?.length || 0;
  const totalModules = learningPath.modules?.length || 0;
  const progressPercent = totalModules > 0 ? (completedCount / totalModules) * 100 : 0;
  const isAllDone = completedCount === totalModules;
  const motivatingMsg = isAllDone
    ? "üåü You've mastered this path! Try a new quiz or review modules."
    : completedCount > 0
    ? "üî• Great job! Keep up the momentum."
    : "üü¢ Let's start learning!";

  return (
    <div className="dashboard-container crazy">
      <div className="dashboard-header crazy-card">
        <h2 className="crazy-title">{learningPath.title} {isAllDone && <span className="crazy-badge">üèÜ</span>}</h2>
        <p className="description">{learningPath.description}</p>
        <div className="stats crazy-stats">
          <div className="stat-item">
            <span className="stat-label">Duration:</span>
            <span className="stat-value">{learningPath.estimatedWeeks} weeks</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Modules:</span>
            <span className="stat-value">{totalModules}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Completed:</span>
            <span className="stat-value">{completedCount}/{totalModules}</span>
          </div>
        </div>
        <div className="progress-bar-container crazy-progress">
          <div className="progress-bar">
            <div
              className="progress-fill crazy-anim"
              style={{ width: `${progressPercent}%` }}
            ></div>
          </div>
          <span className="progress-text">{Math.round(progressPercent)}% Complete</span>
        </div>
        <div className="crazy-motivation">{motivatingMsg}</div>
      </div>
      <div className="modules-list">
        {learningPath.modules && learningPath.modules.map((module, index) => {
          const isCompleted = progress?.completedModules?.includes(index);
          return (
            <div key={index} className={`module-card crazy-card ${isCompleted ? 'completed' : ''}`}>
              <div className="module-header">
                <h3>
                  {isCompleted && <span className="check-mark">‚úÖ</span>}
                  Module {index + 1}: {module.title}
                </h3>
                <span className="module-duration">{module.estimatedHours} hours</span>
              </div>
              <p className="module-description">{module.description}</p>
              {module.topics && module.topics.length > 0 && (
                <div className="topics-list">
                  <strong>Topics:</strong>
                  <ul>
                    {module.topics.map((topic, tIndex) => (
                      <li key={tIndex}>{topic}</li>
                    ))}
                  </ul>
                </div>
              )}
              {!isCompleted && !isAllDone && (
                <button
                  onClick={() => handleCompleteModule(index)}
                  className="complete-btn crazy-btn"
                >
                  Mark as Complete
                </button>
              )}
              {isCompleted && (
                <div className="completed-badge crazy-badge">Completed! üéâ</div>
              )}
            </div>
          );
        })}
      </div>
      <button className="export-csv-btn crazy-btn" onClick={handleExportCSV}>
        üìÅ Export as CSV
      </button>
      <button onClick={onStartQuiz} className="retake-quiz-btn crazy-btn">
        üîÑ Take Another Quiz
      </button>
    </div>
  );
}

export default Dashboard;
