import React, { useState, useEffect } from 'react';
import './AdminPanel.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function AdminPanel({ token, role }) {
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [showTopicForm, setShowTopicForm] = useState(false);
  const [showQuestionForm, setShowQuestionForm] = useState(false);
  const [editingTopic, setEditingTopic] = useState(null);
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [message, setMessage] = useState({ text: '', type: '' });

  const [topicForm, setTopicForm] = useState({ id: '', name: '', description: '' });
  const [questionForm, setQuestionForm] = useState({
    text: '',
    options: ['', '', '', ''],
    correctIndex: 0
  });

  useEffect(() => {
    loadTopics();
  }, []);

  const showMessage = (text, type = 'success') => {
    setMessage({ text, type });
    setTimeout(() => setMessage({ text: '', type: '' }), 3000);
  };

  const loadTopics = async () => {
    try {
      const response = await fetch(`${API_URL}/api/admin/quiz/topics`, {
        headers: { 'Authorization': `Bearer ${token}`, 'Role': role }
      });
      if (response.ok) {
        const data = await response.json();
        setTopics(data);
      }
    } catch (error) {
      showMessage('Failed to load topics', 'error');
    }
  };

  const loadQuestions = async (topicId) => {
    try {
      const response = await fetch(`${API_URL}/api/admin/quiz/topics/${topicId}/questions`, {
        headers: { 'Authorization': `Bearer ${token}`, 'Role': role }
      });
      if (response.ok) {
        const data = await response.json();
        setQuestions(data);
      }
    } catch (error) {
      showMessage('Failed to load questions', 'error');
    }
  };

  const handleSelectTopic = (topic) => {
    setSelectedTopic(topic);
    loadQuestions(topic.id);
  };

  const handleAddTopic = () => {
    setTopicForm({ id: '', name: '', description: '' });
    setEditingTopic(null);
    setShowTopicForm(true);
  };

  const handleEditTopic = (topic) => {
    setTopicForm({ ...topic });
    setEditingTopic(topic);
    setShowTopicForm(true);
  };

  const handleSaveTopic = async () => {
    try {
      const url = editingTopic
        ? `${API_URL}/api/admin/quiz/topics/${editingTopic.id}`
        : `${API_URL}/api/admin/quiz/topics`;
      const method = editingTopic ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
          'Role': role
        },
        body: JSON.stringify(topicForm)
      });

      if (response.ok) {
        showMessage(editingTopic ? 'Topic updated!' : 'Topic added!', 'success');
        setShowTopicForm(false);
        loadTopics();
      } else {
        showMessage('Failed to save topic', 'error');
      }
    } catch (error) {
      showMessage('Error saving topic', 'error');
    }
  };

  const handleDeleteTopic = async (topicId) => {
    if (!window.confirm('Are you sure you want to delete this topic?')) return;
    
    try {
      const response = await fetch(`${API_URL}/api/admin/quiz/topics/${topicId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}`, 'Role': role }
      });

      if (response.ok) {
        showMessage('Topic deleted!', 'success');
        setSelectedTopic(null);
        loadTopics();
      }
    } catch (error) {
      showMessage('Failed to delete topic', 'error');
    }
  };

  const handleAddQuestion = () => {
    setQuestionForm({ text: '', options: ['', '', '', ''], correctIndex: 0 });
    setEditingQuestion(null);
    setShowQuestionForm(true);
  };

  const handleEditQuestion = (question) => {
    setQuestionForm({
      text: question.text,
      options: [...question.options],
      correctIndex: question.correctIndex
    });
    setEditingQuestion(question);
    setShowQuestionForm(true);
  };

  const handleSaveQuestion = async () => {
    try {
      const url = editingQuestion
        ? `${API_URL}/api/admin/quiz/questions/${editingQuestion.id}`
        : `${API_URL}/api/admin/quiz/topics/${selectedTopic.id}/questions`;
      const method = editingQuestion ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
          'Role': role
        },
        body: JSON.stringify(questionForm)
      });

      if (response.ok) {
        showMessage(editingQuestion ? 'Question updated!' : 'Question added!', 'success');
        setShowQuestionForm(false);
        loadQuestions(selectedTopic.id);
      }
    } catch (error) {
      showMessage('Error saving question', 'error');
    }
  };

  const handleDeleteQuestion = async (questionId) => {
    if (!window.confirm('Are you sure you want to delete this question?')) return;

    try {
      const response = await fetch(`${API_URL}/api/admin/quiz/questions/${questionId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}`, 'Role': role }
      });

      if (response.ok) {
        showMessage('Question deleted!', 'success');
        loadQuestions(selectedTopic.id);
      }
    } catch (error) {
      showMessage('Failed to delete question', 'error');
    }
  };

  return (
    <div className="admin-panel">
      {message.text && (
        <div className={`admin-message ${message.type}`}>
          {message.text}
        </div>
      )}

      <div className="admin-header">
        <h1>üõ°Ô∏è Admin Control Panel</h1>
        <p>Manage quiz topics and questions</p>
      </div>

      <div className="admin-content">
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h3>Topics</h3>
            <button className="btn-add" onClick={handleAddTopic}>+ Add Topic</button>
          </div>
          <div className="topics-list">
            {topics.map((topic) => (
              <div
                key={topic.id}
                className={`topic-item ${selectedTopic?.id === topic.id ? 'active' : ''}`}
                onClick={() => handleSelectTopic(topic)}
              >
                <div className="topic-info">
                  <h4>{topic.name}</h4>
                  <p>{topic.description}</p>
                </div>
                <div className="topic-actions">
                  <button onClick={(e) => { e.stopPropagation(); handleEditTopic(topic); }}>‚úèÔ∏è</button>
                  <button onClick={(e) => { e.stopPropagation(); handleDeleteTopic(topic.id); }}>üóëÔ∏è</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="admin-main">
          {selectedTopic ? (
            <>
              <div className="main-header">
                <div>
                  <h2>{selectedTopic.name}</h2>
                  <p>{selectedTopic.description}</p>
                </div>
                <button className="btn-add" onClick={handleAddQuestion}>+ Add Question</button>
              </div>
              <div className="questions-list">
                {questions.map((question, idx) => (
                  <div key={question.id} className="question-card">
                    <div className="question-header">
                      <span className="question-number">Q{idx + 1}</span>
                      <div className="question-actions">
                        <button onClick={() => handleEditQuestion(question)}>‚úèÔ∏è Edit</button>
                        <button onClick={() => handleDeleteQuestion(question.id)}>üóëÔ∏è Delete</button>
                      </div>
                    </div>
                    <div className="question-text">{question.text}</div>
                    <div className="question-options">
                      {question.options.map((option, i) => (
                        <div key={i} className={`option ${i === question.correctIndex ? 'correct' : ''}`}>
                          <span className="option-label">{String.fromCharCode(65 + i)}.</span>
                          <span>{option}</span>
                          {i === question.correctIndex && <span className="correct-badge">‚úì Correct</span>}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </>
          ) : (
            <div className="empty-state">
              <h3>Select a topic to view questions</h3>
              <p>Or create a new topic to get started</p>
            </div>
          )}
        </div>
      </div>

      {showTopicForm && (
        <div className="modal-overlay" onClick={() => setShowTopicForm(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>{editingTopic ? 'Edit Topic' : 'Add New Topic'}</h3>
            <div className="form-group">
              <label>Topic ID</label>
              <input
                type="text"
                value={topicForm.id}
                onChange={(e) => setTopicForm({ ...topicForm, id: e.target.value })}
                placeholder="e.g., javascript"
                disabled={!!editingTopic}
              />
            </div>
            <div className="form-group">
              <label>Topic Name</label>
              <input
                type="text"
                value={topicForm.name}
                onChange={(e) => setTopicForm({ ...topicForm, name: e.target.value })}
                placeholder="e.g., JavaScript Programming"
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                value={topicForm.description}
                onChange={(e) => setTopicForm({ ...topicForm, description: e.target.value })}
                placeholder="Brief description of the topic"
                rows={3}
              />
            </div>
            <div className="modal-actions">
              <button className="btn-cancel" onClick={() => setShowTopicForm(false)}>Cancel</button>
              <button className="btn-save" onClick={handleSaveTopic}>Save</button>
            </div>
          </div>
        </div>
      )}

      {showQuestionForm && (
        <div className="modal-overlay" onClick={() => setShowQuestionForm(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>{editingQuestion ? 'Edit Question' : 'Add New Question'}</h3>
            <div className="form-group">
              <label>Question Text</label>
              <textarea
                value={questionForm.text}
                onChange={(e) => setQuestionForm({ ...questionForm, text: e.target.value })}
                placeholder="Enter your question"
                rows={3}
              />
            </div>
            <div className="form-group">
              <label>Options</label>
              {questionForm.options.map((option, idx) => (
                <input
                  key={idx}
                  type="text"
                  value={option}
                  onChange={(e) => {
                    const newOptions = [...questionForm.options];
                    newOptions[idx] = e.target.value;
                    setQuestionForm({ ...questionForm, options: newOptions });
                  }}
                  placeholder={`Option ${String.fromCharCode(65 + idx)}`}
                />
              ))}
            </div>
            <div className="form-group">
              <label>Correct Answer</label>
              <select
                value={questionForm.correctIndex}
                onChange={(e) => setQuestionForm({ ...questionForm, correctIndex: parseInt(e.target.value) })}
              >
                {questionForm.options.map((_, idx) => (
                  <option key={idx} value={idx}>
                    Option {String.fromCharCode(65 + idx)}
                  </option>
                ))}
              </select>
            </div>
            <div className="modal-actions">
              <button className="btn-cancel" onClick={() => setShowQuestionForm(false)}>Cancel</button>
              <button className="btn-save" onClick={handleSaveQuestion}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
