import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './App.css';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(null);
  const [view, setView] = useState('login');
  const [learningPath, setLearningPath] = useState(null);
  const [progress, setProgress] = useState(null);

  // Load user data from localStorage and API
  const loadUserData = useCallback(async () => {
    if (!currentUser || !token) return;

    // Always fetch progress from backend API
    try {
      const resp = await axios.get(
        `${API_URL}/api/progress/user/${currentUser}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (resp.data) {
        setProgress(resp.data);
        localStorage.setItem('progress', JSON.stringify(resp.data));
      }
    } catch (err) {
      // fallback to localStorage if API fails
      const storedProgress = localStorage.getItem('progress');
      if (storedProgress) setProgress(JSON.parse(storedProgress));
    }

    // Learning path is only in localStorage (unless you build a /api/learning-path endpoint)
    const storedPath = localStorage.getItem('learningPath');
    if (storedPath) setLearningPath(JSON.parse(storedPath));
  }, [currentUser, token]);

  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    const storedToken = localStorage.getItem('token');
    if (storedUser && storedToken) {
      setCurrentUser(storedUser);
      setToken(storedToken);
      setView('dashboard');
    }
  }, []);

  useEffect(() => {
    if (currentUser && token) loadUserData();
  }, [currentUser, token, loadUserData]);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/auth/login`, { username, password });
      setCurrentUser(username);
      setToken(response.data.token);
      localStorage.setItem('currentUser', username);
      localStorage.setItem('token', response.data.token);
      setView('dashboard');
      // After login, load progress from backend!
      await loadUserData();
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.message || 'Login failed' };
    }
  };

  const handleRegister = async (username, password) => {
    try {
      await axios.post(`${API_URL}/api/auth/register`, { username, password });
      return { success: true };
    } catch (error) {
      return { success: false, message: error.response?.data?.message || 'Registration failed' };
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
    setToken(null);
    setView('login');
    // ONLY remove auth info; keep progress and learningPath for persistence
    localStorage.removeItem('currentUser');
    localStorage.removeItem('token');
    // Do NOT remove progress or learningPath!
  };

  const handlePathGenerated = (newPath, newProgress) => {
    setLearningPath(newPath);
    localStorage.setItem('learningPath', JSON.stringify(newPath));
    if (newProgress) {
      setProgress(newProgress);
      localStorage.setItem('progress', JSON.stringify(newProgress));
    }
    setView('dashboard');
  };

  const handleProgressUpdate = async (updatedProgress) => {
    try {
      const response = await axios.post(
        `${API_URL}/api/progress/update`,
        updatedProgress,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProgress(response.data);
      localStorage.setItem('progress', JSON.stringify(response.data));
      return { success: true };
    } catch (error) {
      return { success: false, message: 'Failed to update progress' };
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Learning Path Generator</h1>
        {currentUser && (
          <div className="user-info">
            <span>Welcome, {currentUser}!</span>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </div>
        )}
      </header>
      <main>
        {!currentUser && view === 'login' && (
          <Login onLogin={handleLogin} onSwitchToRegister={() => setView('register')} />
        )}
        {!currentUser && view === 'register' && (
          <Register onRegister={handleRegister} onSwitchToLogin={() => setView('login')} />
        )}
        {currentUser && view === 'dashboard' && (
          <Dashboard
            learningPath={learningPath}
            progress={progress}
            onStartQuiz={() => setView('quiz')}
            onProgressUpdate={handleProgressUpdate}
          />
        )}
        {currentUser && view === 'quiz' && (
          <Quiz
            currentUser={currentUser}
            token={token}
            onBack={() => setView('dashboard')}
            onPathGenerated={handlePathGenerated}
          />
        )}
      </main>
    </div>
  );
}

export default App;
