import React, { useState } from 'react';
import './Dashboard.css';

function Dashboard({ learningPath, progress, role, onStartQuiz, onProgressUpdate }) {
  const [adminTopicTitle, setAdminTopicTitle] = useState('');
  const [adminTopicDesc, setAdminTopicDesc] = useState('');
  const [adminMessage, setAdminMessage] = useState('');

  const handleExportCSV = () => {
    if (!learningPath || !learningPath.modules) return;
    const csvRows = [
      ['Module #', 'Title', 'Description', 'Hours', 'Topics']
    ];
    learningPath.modules.forEach((module, idx) => {
      csvRows.push([
        idx + 1,
        `"${module.title}"`,
        `"${module.description}"`,
        module.hours,
        `"${(module.topics || []).join('; ')}"`
      ]);
    });
    const csvContent = csvRows.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${learningPath.title.replace(/\s+/g, '_')}_modules.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  // Animated confetti
  const showConfetti = () => {
    const canvas = document.createElement('canvas');
    canvas.className = 'confetti-canvas';
    document.body.appendChild(canvas);
    const ctx = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const pieces = Array.from({ length: 80 }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height * 0.2,
      r: Math.random() * 8 + 4,
      c: `hsl(${Math.random()*360},100%,50%)`,
      s: Math.random() * 2 + 2
    }));
    let frame = 0;
    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      pieces.forEach(p => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, 2*Math.PI);
        ctx.fillStyle = p.c;
        ctx.fill();
        p.y += p.s;
        p.x += Math.sin(frame/10 + p.y/20) * 2;
      });
      frame++;
      if (frame < 60) requestAnimationFrame(draw);
      else document.body.removeChild(canvas);
    }
    draw();
  };

  const handleCompleteModule = async (moduleIndex) => {
    if (!progress || !learningPath) return;
    const completedModules = progress.completedModules || [];
    if (completedModules.includes(moduleIndex)) {
      alert('Module already completed!');
      return;
    }
    const newCompletedModules = [...completedModules, moduleIndex];
    const newProgressPercent = (newCompletedModules.length / progress.totalModules) * 100;
    const updatedProgress = {
      ...progress,
      completedModules: newCompletedModules,
      currentModule: moduleIndex + 1,
      overallProgress: newProgressPercent
    };
    const result = await onProgressUpdate(updatedProgress);
    if (result.success) {
      showConfetti();
    } else {
      alert('Failed to update progress');
    }
  };

  // Admin Panel Functions
  async function handleAdminAddTopic() {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';
      const token = localStorage.getItem('token');
      const roleHeader = localStorage.getItem('role');
      const response = await fetch(`${API_URL}/api/admin/quiz/topics`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
          'Role': roleHeader
        },
        body: JSON.stringify({
          id: adminTopicTitle.replace(/\s+/g, '-').toLowerCase(),
          name: adminTopicTitle,
          description: adminTopicDesc
        })
      });
      if (response.ok) {
        setAdminMessage('‚úÖ Topic added successfully!');
        setAdminTopicTitle('');
        setAdminTopicDesc('');
      } else {
        setAdminMessage('‚ùå Failed to add topic.');
      }
    } catch (e) {
      setAdminMessage('‚ùå Error: ' + e.message);
    }
  }

  if (!learningPath) {
    return (
      <div className="dashboard-container crazy-bg">
        <div className="no-path-card crazy-card fade-in">
          <h2>üéì Welcome, Future Expert!</h2>
          <p>You haven't started a learning path yet.</p>
          <p>Take a quiz to unlock your journey!</p>
          <button onClick={onStartQuiz} className="start-quiz-btn creative-btn pulse-btn">
            üöÄ Take a Quiz
          </button>
        </div>

        {/* Admin Section - Show even when no learning path */}
        {role === "ADMIN" && (
          <div className="admin-section crazy-card fade-in" style={{
            margin: "2rem auto",
            maxWidth: "600px",
            padding: "2rem",
            background: "linear-gradient(135deg, #667eea15 0%, #764ba215 100%)",
            borderRadius: "16px",
            border: "2px solid #667eea"
          }}>
            <h2 style={{color: "#667eea", marginBottom: "1rem"}}>üõ°Ô∏è Admin Quick Actions</h2>
            <p style={{marginBottom: "1.5rem", color: "#6b7280"}}>
              Add topics and questions for quizzes
            </p>
            <div style={{marginBottom: "1rem"}}>
              <input
                type="text"
                placeholder="Topic Title (e.g., JavaScript Basics)"
                value={adminTopicTitle}
                onChange={e => setAdminTopicTitle(e.target.value)}
                style={{
                  width: "100%",
                  padding: "0.8rem",
                  marginBottom: "0.8rem",
                  borderRadius: "8px",
                  border: "2px solid #e5e7eb",
                  fontSize: "1rem"
                }}
              />
              <textarea
                rows={3}
                placeholder="Topic Description"
                value={adminTopicDesc}
                onChange={e => setAdminTopicDesc(e.target.value)}
                style={{
                  width: "100%",
                  padding: "0.8rem",
                  marginBottom: "0.8rem",
                  borderRadius: "8px",
                  border: "2px solid #e5e7eb",
                  fontSize: "1rem",
                  fontFamily: "inherit"
                }}
              />
              <button
                className="creative-btn pulse-btn"
                onClick={handleAdminAddTopic}
                disabled={!adminTopicTitle}
                style={{width: "100%"}}
              >
                ‚ûï Add Topic
              </button>
            </div>
            {adminMessage && (
              <div style={{
                padding: "1rem",
                borderRadius: "8px",
                background: adminMessage.includes('‚úÖ') ? '#d1fae5' : '#fee2e2',
                color: adminMessage.includes('‚úÖ') ? '#065f46' : '#991b1b',
                fontWeight: "600",
                marginTop: "1rem"
              }}>
                {adminMessage}
              </div>
            )}
            <p style={{marginTop: "1.5rem", color: "#6b7280", fontSize: "0.9rem"}}>
              üí° <strong>Tip:</strong> Use the Admin Panel in the header for full topic and question management.
            </p>
          </div>
        )}
      </div>
    );
  }

  const completedCount = progress?.completedModules?.length || 0;
  const totalModules = learningPath.modules?.length || 0;
  const progressPercent = totalModules > 0 ? (completedCount / totalModules) * 100 : 0;
  const isAllDone = completedCount === totalModules;
  const motivatingMsg = isAllDone
    ? "üåü You've mastered this path! Try a new quiz or review modules."
    : completedCount > 0
    ? "üî• Great job! Keep up the momentum."
    : "üü¢ Let's start learning!";

  return (
    <div className="dashboard-container crazy-bg">
      <div className="dashboard-header crazy-card fade-in">
        <h2 className="crazy-title-glow">{learningPath.title} {isAllDone && <span className="crazy-badge-glow">üèÜ</span>}</h2>
        <p className="description">{learningPath.description}</p>
        <div className="stats crazy-stats">
          <div className="stat-item">
            <span className="stat-label">Duration:</span>
            <span className="stat-value">{learningPath.estimatedWeeks} weeks</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Modules:</span>
            <span className="stat-value">{totalModules}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Completed:</span>
            <span className="stat-value">{completedCount}/{totalModules}</span>
          </div>
        </div>
        <div className="progress-bar-container crazy-progress-glow">
          <div className="progress-bar animated-bar-glow">
            <div
              className="progress-fill crazy-anim-glow"
              style={{ width: `${progressPercent}%` }}
            ></div>
          </div>
          <span className="progress-text">{Math.round(progressPercent)}% Complete</span>
        </div>
        <div className="crazy-motivation">{motivatingMsg}</div>
      </div>

      <div className="modules-list">
        {learningPath.modules && learningPath.modules.map((module, index) => {
          const isCompleted = progress?.completedModules?.includes(index);
          return (
            <div key={index} className={`module-card crazy-card module-pop ${isCompleted ? 'completed card-glow' : ''}`}>
              <div className="module-header">
                <h3>
                  {isCompleted && <span className="check-mark">‚úÖ</span>}
                  Module {index + 1}: {module.title}
                </h3>
                <span className="module-duration">
                  {module.hours ? `${module.hours} hours` : "Hours not specified"}
                </span>
              </div>
              <p className="module-description">{module.description}</p>
              {module.topics && module.topics.length > 0 && (
                <div className="topics-list">
                  <strong>Topics:</strong>
                  <ul>
                    {module.topics.map((topic, tIndex) => (
                      <li key={tIndex}>{topic}</li>
                    ))}
                  </ul>
                </div>
              )}
              {!isCompleted && !isAllDone && (
                <button
                  onClick={() => handleCompleteModule(index)}
                  className="complete-btn creative-btn pulse-btn"
                >
                  <span role="img" aria-label="spark">‚ú®</span> Mark as Complete
                </button>
              )}
              {isCompleted && (
                <div className="completed-badge crazy-badge-glow">Completed! üéâ</div>
              )}
            </div>
          );
        })}
      </div>

      <div style={{display: 'flex', gap: '1rem', flexWrap: 'wrap', justifyContent: 'center', marginTop: '2rem'}}>
        <button className="export-csv-btn creative-btn pulse-btn" onClick={handleExportCSV}>
          <span role="img" aria-label="csv">üìÅ</span> Export as CSV
        </button>
        <button onClick={onStartQuiz} className="retake-quiz-btn creative-btn pulse-btn">
          <span role="img" aria-label="quiz">üîÑ</span> Take Another Quiz
        </button>
      </div>

      {/* Admin Section at Bottom */}
      {role === "ADMIN" && (
        <div className="admin-section crazy-card fade-in" style={{
          margin: "3rem auto 2rem",
          maxWidth: "600px",
          padding: "2rem",
          background: "linear-gradient(135deg, #667eea15 0%, #764ba215 100%)",
          borderRadius: "16px",
          border: "2px solid #667eea"
        }}>
          <h2 style={{color: "#667eea", marginBottom: "1rem"}}>üõ°Ô∏è Admin Quick Actions</h2>
          <div style={{marginBottom: "1rem"}}>
            <input
              type="text"
              placeholder="Topic Title"
              value={adminTopicTitle}
              onChange={e => setAdminTopicTitle(e.target.value)}
              style={{
                width: "100%",
                padding: "0.8rem",
                marginBottom: "0.8rem",
                borderRadius: "8px",
                border: "2px solid #e5e7eb",
                fontSize: "1rem"
              }}
            />
            <textarea
              rows={3}
              placeholder="Topic Description"
              value={adminTopicDesc}
              onChange={e => setAdminTopicDesc(e.target.value)}
              style={{
                width: "100%",
                padding: "0.8rem",
                marginBottom: "0.8rem",
                borderRadius: "8px",
                border: "2px solid #e5e7eb",
                fontSize: "1rem",
                fontFamily: "inherit"
              }}
            />
            <button
              className="creative-btn pulse-btn"
              onClick={handleAdminAddTopic}
              disabled={!adminTopicTitle}
              style={{width: "100%"}}
            >
              ‚ûï Add Topic
            </button>
          </div>
          {adminMessage && (
            <div style={{
              padding: "1rem",
              borderRadius: "8px",
              background: adminMessage.includes('‚úÖ') ? '#d1fae5' : '#fee2e2',
              color: adminMessage.includes('‚úÖ') ? '#065f46' : '#991b1b',
              fontWeight: "600"
            }}>
              {adminMessage}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
