import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  Container, Paper, Typography, TextField, Button, Box, Alert,
  AppBar, Toolbar, Tabs, Tab, Card, CardContent, Grid,
  Radio, RadioGroup, FormControlLabel, FormControl, Chip, Divider, LinearProgress,
} from '@mui/material';
import { School, Quiz as QuizIcon, Dashboard as DashboardIcon, Timeline, CheckCircle, GetApp, PlayArrow } from '@mui/icons-material';
import axios from 'axios';

const theme = createTheme({
  palette: {
    primary: { main: '#2196f3' },
    secondary: { main: '#f50057' },
    success: { main: '#4caf50' },
  },
});

function App() {
  // Auth state
  const [authMode, setAuthMode] = useState('login');
  const [currentTab, setCurrentTab] = useState(0);
  const [token, setToken] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [validatingToken, setValidatingToken] = useState(true);

  // Learning state
  const [learningPath, setLearningPath] = useState(null);
  const [progress, setProgress] = useState({ completedModules: [], currentModule: 0, overallProgress: 0 });

  // Quiz state
  const [topics, setTopics] = useState([]);
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState({});
  const [quizResult, setQuizResult] = useState(null);
  const [quizForm, setQuizForm] = useState({ name: '', target: '', weeklyHours: 10 });

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

  // Validate token on app load
  useEffect(() => {
    const validateStoredToken = async () => {
      const storedToken = localStorage.getItem('token');
      const storedUser = localStorage.getItem('currentUser');

      console.log('Checking stored token...');
      console.log('Stored user:', storedUser);

      if (storedToken && storedUser) {
        try {
          // Try to validate token with a simple API call
          const response = await axios.get(
            `${API_URL}/api/quiz/topics`,
            { 
              headers: { Authorization: `Bearer ${storedToken}` },
              timeout: 5000 
            }
          );

          // Token is valid
          console.log('Token is valid, auto-logging in as:', storedUser);
          setToken(storedToken);
          setCurrentUser(storedUser);
          setQuizForm({ name: storedUser, target: '', weeklyHours: 10 });
        } catch (err) {
          // Token is invalid or expired
          console.log('Stored token is invalid, clearing localStorage');
          localStorage.removeItem('token');
          localStorage.removeItem('currentUser');
          setToken(null);
          setCurrentUser(null);
        }
      } else {
        console.log('No stored credentials found');
      }

      setValidatingToken(false);
    };

    validateStoredToken();
  }, [API_URL]);

  // Load user data when logged in
  useEffect(() => {
    if (token && currentUser && !validatingToken) {
      loadUserData();
    }
  }, [token, currentUser, validatingToken]);

  // Fetch topics when on quiz tab
  useEffect(() => {
    if (token && currentTab === 1 && topics.length === 0) {
      fetchTopics();
    }
  }, [currentTab, token]);

  // Function to load user-specific data
  const loadUserData = async () => {
  if (!currentUser || !token) return;

  try {
    const response = await axios.get(
      `${API_URL}/api/quiz/user-data/${currentUser}`,  // â† Changed path
      { headers: { Authorization: `Bearer ${token}` } }
    );

    if (response && response.data) {
      if (response.data.learningPath) {
        setLearningPath(response.data.learningPath);
      }
      if (response.data.progress) {
        setProgress(response.data.progress);
      }
    }
  } catch (err) {
    console.error('Error:', err);
  }
};

  const fetchTopics = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/quiz/topics`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTopics(Array.from(response.data));
    } catch (err) {
      console.error('Failed to fetch quiz topics:', err);
      setError('Failed to fetch quiz topics');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_URL}/api/auth/login`, { username, password });

      const newToken = response.data.token;
      const loginUsername = username;

      setToken(newToken);
      setCurrentUser(loginUsername);

      // Store in localStorage
      localStorage.setItem('token', newToken);
      localStorage.setItem('currentUser', loginUsername);

      console.log('Login successful for user:', loginUsername);

      setQuizForm({ name: loginUsername, target: '', weeklyHours: 10 });

      // Clear form
      setUsername('');
      setPassword('');

      // Load user data after a short delay
      setTimeout(() => {
        loadUserDataAfterLogin(loginUsername, newToken);
      }, 200);

    } catch (err) {
      console.error('Login error:', err);
      setError('Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const loadUserDataAfterLogin = async (username, authToken) => {
    try {
      console.log('Loading data for user after login:', username);

      // Fetch learning path
      const pathResponse = await axios.get(
        `${API_URL}/api/learning-path/user/${username}`,
        { headers: { Authorization: `Bearer ${authToken}` } }
      ).catch(() => null);

      if (pathResponse && pathResponse.data) {
        console.log('Learning path loaded after login');
        setLearningPath(pathResponse.data);

        // Fetch progress
        const progressResponse = await axios.get(
          `${API_URL}/api/progress/user/${username}`,
          { headers: { Authorization: `Bearer ${authToken}` } }
        ).catch(() => null);

        if (progressResponse && progressResponse.data) {
          console.log('Progress loaded after login');
          setProgress(progressResponse.data);
        } else {
          setProgress({
            completedModules: [],
            currentModule: 0,
            overallProgress: 0,
            totalModules: pathResponse.data.modules?.length || 0
          });
        }
      }
    } catch (err) {
      console.error('Error loading data after login:', err);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await axios.post(`${API_URL}/api/auth/register`, { username, password });
      setSuccess('Registration successful! You can now login.');
      setAuthMode('login');
      setPassword('');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Registration error:', err);
      setError('Registration failed. Username may already exist.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    console.log('Logging out user:', currentUser);

    // Clear ALL state
    setToken(null);
    setCurrentUser(null);
    setLearningPath(null);
    setProgress({ completedModules: [], currentModule: 0, overallProgress: 0 });
    setQuiz(null);
    setQuizResult(null);
    setAnswers({});
    setTopics([]);
    setCurrentTab(0);
    setAuthMode('login');
    setUsername('');
    setPassword('');
    setQuizForm({ name: '', target: '', weeklyHours: 10 });

    // Clear localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('currentUser');

    console.log('Logout complete - localStorage cleared');
  };

  const loadQuiz = async (topicId) => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`${API_URL}/api/quiz/${topicId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuiz(response.data);
      setAnswers({});
      setQuizResult(null);
    } catch (err) {
      console.error('Failed to load quiz:', err);
      setError('Failed to load quiz');
    } finally {
      setLoading(false);
    }
  };

  const submitQuiz = async () => {
    if (Object.keys(answers).length < quiz.questions.length) {
      setError('Please answer all questions');
      return;
    }
    setLoading(true);
    setError('');

    try {
      const payload = {
        answers,
        weeklyHours: parseInt(quizForm.weeklyHours),
        name: quizForm.name || currentUser,
        target: quizForm.target
      };

      console.log('Submitting quiz with payload:', payload);

      const response = await axios.post(
        `${API_URL}/api/quiz/${quiz.topicId}/submit`,
        payload,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      console.log('Quiz response received:', response.data);

      setQuizResult(response.data);
      const newLearningPath = response.data.learningPath;
      setLearningPath(newLearningPath);

      console.log('Learning path set in state');

      // Initialize progress
      const totalModules = newLearningPath.modules?.length || 0;
      const initialProgress = {
        username: currentUser,
        completedModules: [],
        currentModule: 0,
        overallProgress: 0,
        totalModules: totalModules
      };

      setProgress(initialProgress);

      console.log('Progress initialized');

      setSuccess('Quiz completed! Learning path generated.');

      // Redirect to dashboard after 2 seconds
      setTimeout(() => { 
        setCurrentTab(0); 
        setSuccess(''); 
        setQuiz(null);
        setQuizResult(null);
        setAnswers({});
      }, 2000);

    } catch (err) {
      console.error('Quiz submission error:', err);
      setError('Failed to submit quiz: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const markModuleComplete = async (idx) => {
    if (progress.completedModules && progress.completedModules.includes(idx)) return;

    const currentCompleted = progress.completedModules || [];
    const updated = [...currentCompleted, idx];
    const totalModules = learningPath.modules?.length || 1;
    const newProgress = {
      username: currentUser,
      completedModules: updated,
      currentModule: idx + 1,
      overallProgress: (updated.length / totalModules) * 100,
      totalModules: totalModules
    };

    setProgress(newProgress);

    // Save to backend
    try {
      await axios.post(
        `${API_URL}/api/progress/update`,
        newProgress,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log('Progress updated on server');
      setSuccess(`Module ${idx + 1} completed!`);
      setTimeout(() => setSuccess(''), 2000);
    } catch (err) {
      console.error('Failed to update progress:', err);
      setError('Failed to update progress');
      // Revert on error
      setProgress({
        ...progress,
        completedModules: currentCompleted
      });
    }
  };

  const generatePath = async () => {
    setLoading(true);
    setError('');
    try {
      const response = await axios.post(
        `${API_URL}/api/learning-path/generate`,
        { 
          name: currentUser, 
          skillLevel: 'INTERMEDIATE', 
          interests: ['java', 'spring'], 
          target: 'Backend Developer', 
          weeklyHours: 15 
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const newLearningPath = response.data;
      setLearningPath(newLearningPath);

      console.log('Learning path generated');

      // Save to backend
      try {
        await axios.post(
          `${API_URL}/api/learning-path/save`,
          {
            username: currentUser,
            learningPath: newLearningPath
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Learning path saved to database');
      } catch (saveErr) {
        console.error('Failed to save learning path:', saveErr);
      }

      // Initialize progress
      const totalModules = newLearningPath.modules?.length || 0;
      const initialProgress = {
        username: currentUser,
        completedModules: [],
        currentModule: 0,
        overallProgress: 0,
        totalModules: totalModules
      };

      setProgress(initialProgress);

      // Save progress to backend
      try {
        await axios.post(
          `${API_URL}/api/progress/save`,
          initialProgress,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Progress initialized on server');
      } catch (progressErr) {
        console.error('Failed to save progress:', progressErr);
      }

      setSuccess('Learning path generated!');
      setTimeout(() => setSuccess(''), 2000);
    } catch (err) {
      console.error('Failed to generate path:', err);
      setError('Failed to generate path: ' + (err.response?.data?.error || err.message));
    } finally {
      setLoading(false);
    }
  };

  const exportToCSV = async () => {
    if (!learningPath) return;
    try {
      const response = await axios.post(`${API_URL}/api/export/learning-path/csv`, learningPath, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `learning-path-${currentUser}-${Date.now()}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
      setSuccess('Exported successfully!');
      setTimeout(() => setSuccess(''), 2000);
    } catch (err) {
      console.error('Export failed:', err);
      setError('Export failed');
    }
  };

  // Show loading while validating token
  if (validatingToken) {
    return (
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Box sx={{ 
          minHeight: '100vh', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
        }}>
          <Paper elevation={6} sx={{ p: 4, textAlign: 'center' }}>
            <School sx={{ fontSize: 64, color: 'primary.main', mb: 2 }} />
            <Typography variant="h5">Loading...</Typography>
            <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
              Checking authentication
            </Typography>
          </Paper>
        </Box>
      </ThemeProvider>
    );
  }

  // ============================================
  // LOGIN/REGISTER PAGE
  // ============================================
  if (!token) {
    return (
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Box sx={{ 
          minHeight: '100vh', 
          display: 'flex', 
          alignItems: 'center', 
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
        }}>
          <Container maxWidth="sm">
            <Paper elevation={6} sx={{ p: 4, borderRadius: 3 }}>
              <Box sx={{ textAlign: 'center', mb: 3 }}>
                <School sx={{ fontSize: 64, color: 'primary.main', mb: 2 }} />
                <Typography variant="h4" gutterBottom fontWeight="bold">
                  Learning Path Generator
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  AI-Powered Personalized Learning
                </Typography>
              </Box>

              <Tabs 
                value={authMode === 'login' ? 0 : 1} 
                onChange={(e, val) => {
                  setAuthMode(val === 0 ? 'login' : 'register');
                  setError('');
                  setSuccess('');
                }}
                centered
                sx={{ mb: 3 }}
              >
                <Tab label="Login" />
                <Tab label="Register" />
              </Tabs>

              {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
              {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

              {authMode === 'login' && (
                <Box component="form" onSubmit={handleLogin}>
                  <TextField
                    fullWidth
                    label="Username"
                    variant="outlined"
                    margin="normal"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    autoComplete="username"
                  />
                  <TextField
                    fullWidth
                    label="Password"
                    type="password"
                    variant="outlined"
                    margin="normal"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    autoComplete="current-password"
                  />
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    size="large"
                    disabled={loading}
                    sx={{ mt: 3, py: 1.5 }}
                  >
                    {loading ? 'Signing in...' : 'LOGIN'}
                  </Button>
                </Box>
              )}

              {authMode === 'register' && (
                <Box component="form" onSubmit={handleRegister}>
                  <TextField
                    fullWidth
                    label="Username"
                    variant="outlined"
                    margin="normal"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    autoComplete="username"
                    helperText="Choose a unique username"
                  />
                  <TextField
                    fullWidth
                    label="Password"
                    type="password"
                    variant="outlined"
                    margin="normal"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    autoComplete="new-password"
                    helperText="Minimum 4 characters"
                    inputProps={{ minLength: 4 }}
                  />
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    size="large"
                    disabled={loading}
                    sx={{ mt: 3, py: 1.5 }}
                  >
                    {loading ? 'Creating account...' : 'REGISTER'}
                  </Button>
                </Box>
              )}

              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Typography variant="body2" color="textSecondary">
                  {authMode === 'login' 
                    ? "Don't have an account? Switch to Register tab" 
                    : "Already have an account? Switch to Login tab"}
                </Typography>
              </Box>
            </Paper>
          </Container>
        </Box>
      </ThemeProvider>
    );
  }

  // ============================================
  // MAIN APPLICATION (AFTER LOGIN)
  // ============================================
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />

      <AppBar position="static">
        <Toolbar>
          <School sx={{ mr: 2 }} />
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Learning Path Generator
          </Typography>
          <Typography variant="body1" sx={{ mx: 2 }}>
            Welcome, {currentUser}!
          </Typography>
          <Button color="inherit" onClick={handleLogout}>
            LOGOUT
          </Button>
        </Toolbar>
      </AppBar>

      <Box sx={{ borderBottom: 1, borderColor: 'divider', bgcolor: 'white' }}>
        <Tabs value={currentTab} onChange={(e, val) => setCurrentTab(val)} centered>
          <Tab icon={<DashboardIcon />} label="Dashboard" />
          <Tab icon={<QuizIcon />} label="Take Quiz" />
          <Tab icon={<Timeline />} label="Learning Path" />
        </Tabs>
      </Box>

      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        {error && <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>{success}</Alert>}

        {/* TAB 0: DASHBOARD */}
        {currentTab === 0 && (
          <Paper sx={{ p: 4 }}>
            <Typography variant="h4" gutterBottom>ðŸ“Š Dashboard</Typography>
            {!learningPath ? (
              <Box>
                <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
                  Get Started with Your Learning Journey
                </Typography>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Card sx={{ height: '100%' }}>
                      <CardContent sx={{ textAlign: 'center', p: 4 }}>
                        <QuizIcon sx={{ fontSize: 60, color: 'primary.main', mb: 2 }} />
                        <Typography variant="h5" gutterBottom>Take Assessment Quiz</Typography>
                        <Typography variant="body2" color="textSecondary" paragraph>
                          Test your knowledge and get a personalized learning path
                        </Typography>
                        <Button 
                          variant="contained" 
                          size="large" 
                          startIcon={<PlayArrow />} 
                          onClick={() => setCurrentTab(1)}
                          sx={{ mt: 2 }}
                        >
                          Start Quiz
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Card sx={{ height: '100%' }}>
                      <CardContent sx={{ textAlign: 'center', p: 4 }}>
                        <Timeline sx={{ fontSize: 60, color: 'success.main', mb: 2 }} />
                        <Typography variant="h5" gutterBottom>Quick Generate</Typography>
                        <Typography variant="body2" color="textSecondary" paragraph>
                          Generate a default intermediate-level path
                        </Typography>
                        <Button 
                          variant="contained" 
                          size="large" 
                          color="success" 
                          onClick={generatePath} 
                          disabled={loading}
                          sx={{ mt: 2 }}
                        >
                          {loading ? 'Generating...' : 'Generate Now'}
                        </Button>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>
              </Box>
            ) : (
              <Box>
                {/* Progress Stats */}
                <Grid container spacing={2} sx={{ mb: 4 }}>
                  <Grid item xs={12} md={3}>
                    <Card sx={{ bgcolor: 'primary.main', color: 'white' }}>
                      <CardContent sx={{ textAlign: 'center' }}>
                        <Typography variant="h3" fontWeight="bold">
                          {progress.overallProgress ? progress.overallProgress.toFixed(0) : 0}%
                        </Typography>
                        <Typography variant="body2">Overall Progress</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} md={3}>
                    <Card>
                      <CardContent sx={{ textAlign: 'center' }}>
                        <Typography variant="h3" fontWeight="bold" color="success.main">
                          {progress.completedModules ? progress.completedModules.length : 0}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">Completed</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} md={3}>
                    <Card>
                      <CardContent sx={{ textAlign: 'center' }}>
                        <Typography variant="h3" fontWeight="bold" color="primary.main">
                          {learningPath.modules?.length || 0}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">Total Modules</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} md={3}>
                    <Card>
                      <CardContent sx={{ textAlign: 'center' }}>
                        <Typography variant="h3" fontWeight="bold" color="secondary.main">
                          {learningPath.weeklyHours || 0}h
                        </Typography>
                        <Typography variant="body2" color="textSecondary">Per Week</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>

                <Box sx={{ mb: 4 }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="body2" fontWeight="bold">Learning Progress</Typography>
                    <Typography variant="body2" color="textSecondary">
                      {progress.completedModules ? progress.completedModules.length : 0} / {learningPath.modules?.length || 0} modules
                    </Typography>
                  </Box>
                  <LinearProgress 
                    variant="determinate" 
                    value={progress.overallProgress || 0} 
                    sx={{ height: 10, borderRadius: 5 }}
                  />
                </Box>

                <Typography variant="h5" gutterBottom sx={{ mt: 4 }}>Your Learning Modules</Typography>
                <Divider sx={{ mb: 3 }} />

                {learningPath.modules?.map((module, idx) => {
                  const isCompleted = progress.completedModules && progress.completedModules.includes(idx);
                  const isCurrent = progress.currentModule === idx && !isCompleted;

                  return (
                    <Card 
                      key={idx} 
                      sx={{ 
                        mb: 2,
                        bgcolor: isCompleted ? 'success.light' : 'white',
                        border: isCurrent ? 2 : 1,
                        borderColor: isCurrent ? 'primary.main' : 'divider'
                      }}
                    >
                      <CardContent>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                          <Box sx={{ flex: 1 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              {isCompleted && <CheckCircle sx={{ fontSize: 20, mr: 1, color: 'success.dark' }} />}
                              {isCurrent && <Chip label="Current" color="primary" size="small" sx={{ mr: 1 }} />}
                              <Typography variant="h6" color={isCompleted ? 'success.dark' : 'primary.main'}>
                                {idx + 1}. {module.title}
                              </Typography>
                            </Box>
                            <Typography variant="body2" color="textSecondary" paragraph>
                              {module.description}
                            </Typography>
                            <Typography variant="caption" color="textSecondary">
                              â±ï¸ {module.hours} hours
                            </Typography>
                            {module.resources && module.resources.length > 0 && (
                              <Box sx={{ mt: 2 }}>
                                <Typography variant="subtitle2" gutterBottom>Resources:</Typography>
                                {module.resources.map((res, rIdx) => (
                                  <Chip key={rIdx} label={res} size="small" sx={{ mr: 1, mb: 1 }} />
                                ))}
                              </Box>
                            )}
                          </Box>
                          {!isCompleted && (
                            <Button
                              variant="contained"
                              color="success"
                              size="small"
                              startIcon={<CheckCircle />}
                              onClick={() => markModuleComplete(idx)}
                            >
                              Complete
                            </Button>
                          )}
                        </Box>
                      </CardContent>
                    </Card>
                  );
                })}

                <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
                  <Button variant="outlined" onClick={() => setCurrentTab(2)}>
                    View Full Learning Path
                  </Button>
                  <Button variant="contained" color="success" startIcon={<GetApp />} onClick={exportToCSV}>
                    Export to CSV
                  </Button>
                </Box>
              </Box>
            )}
          </Paper>
        )}

        {/* TAB 1: QUIZ */}
        {currentTab === 1 && (
          <Paper sx={{ p: 4 }}>
            <Typography variant="h4" gutterBottom>ðŸ“ Skill Assessment Quiz</Typography>
            <Typography variant="body1" color="textSecondary" paragraph>
              Test your knowledge and get a personalized learning path
            </Typography>

            {!quiz && !quizResult && (
              <Box sx={{ mt: 4 }}>
                <Typography variant="h5" gutterBottom>Select a Quiz Topic</Typography>
                {topics.length === 0 ? (
                  <Box sx={{ textAlign: 'center', py: 4 }}>
                    <Typography color="textSecondary">Loading quiz topics...</Typography>
                  </Box>
                ) : (
                  <Grid container spacing={2} sx={{ mt: 2 }}>
                    {topics.map(topicId => (
                      <Grid item xs={12} sm={6} md={4} key={topicId}>
                        <Card 
                          sx={{ 
                            cursor: 'pointer',
                            transition: 'all 0.3s',
                            '&:hover': { transform: 'translateY(-5px)', boxShadow: 6 }
                          }}
                          onClick={() => loadQuiz(topicId)}
                        >
                          <CardContent sx={{ textAlign: 'center', py: 4 }}>
                            <QuizIcon sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                            <Typography variant="h6">
                              {topicId.replace('t-', '').replace(/-/g, ' ').toUpperCase()}
                            </Typography>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                )}
              </Box>
            )}

            {quiz && !quizResult && (
              <Box sx={{ mt: 4 }}>
                <Typography variant="h5" gutterBottom>{quiz.topicName}</Typography>

                <Box sx={{ mb: 4, p: 3, bgcolor: 'background.default', borderRadius: 2 }}>
                  <Typography variant="h6" gutterBottom>Your Information</Typography>
                  <Grid container spacing={2}>
                    <Grid item xs={12} md={4}>
                      <TextField 
                        fullWidth 
                        label="Name" 
                        value={quizForm.name || currentUser} 
                        onChange={(e) => setQuizForm({...quizForm, name: e.target.value})} 
                      />
                    </Grid>
                    <Grid item xs={12} md={4}>
                      <TextField 
                        fullWidth 
                        label="Career Target" 
                        placeholder="e.g., Backend Developer" 
                        value={quizForm.target} 
                        onChange={(e) => setQuizForm({...quizForm, target: e.target.value})} 
                      />
                    </Grid>
                    <Grid item xs={12} md={4}>
                      <TextField 
                        fullWidth 
                        type="number" 
                        label="Weekly Hours" 
                        value={quizForm.weeklyHours} 
                        onChange={(e) => setQuizForm({...quizForm, weeklyHours: e.target.value})} 
                        inputProps={{ min: 1, max: 40 }} 
                      />
                    </Grid>
                  </Grid>
                </Box>

                {quiz.questions?.map((q, idx) => (
                  <Card key={q.id} sx={{ mb: 3 }}>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        Question {idx + 1}: {q.text}
                      </Typography>
                      <FormControl component="fieldset" fullWidth>
                        <RadioGroup 
                          value={answers[q.id] !== undefined ? answers[q.id] : ''} 
                          onChange={(e) => setAnswers({...answers, [q.id]: parseInt(e.target.value)})}
                        >
                          {q.options?.map((opt, optIdx) => (
                            <FormControlLabel 
                              key={optIdx} 
                              value={optIdx} 
                              control={<Radio />} 
                              label={opt}
                              sx={{ 
                                p: 1.5,
                                m: 0.5,
                                border: 1,
                                borderColor: 'divider',
                                borderRadius: 1,
                                '&:hover': { bgcolor: 'action.hover' }
                              }}
                            />
                          ))}
                        </RadioGroup>
                      </FormControl>
                    </CardContent>
                  </Card>
                ))}

                <Box sx={{ display: 'flex', gap: 2 }}>
                  <Button variant="outlined" onClick={() => { setQuiz(null); setAnswers({}); }}>
                    Back to Topics
                  </Button>
                  <Button 
                    variant="contained" 
                    size="large" 
                    onClick={submitQuiz} 
                    disabled={loading || Object.keys(answers).length < quiz.questions?.length}
                  >
                    {loading ? 'Submitting...' : `Submit Quiz (${Object.keys(answers).length}/${quiz.questions?.length})`}
                  </Button>
                </Box>
              </Box>
            )}

            {quizResult && (
              <Box sx={{ mt: 4 }}>
                <Card sx={{ mb: 3, bgcolor: 'success.main', color: 'white' }}>
                  <CardContent sx={{ textAlign: 'center', py: 4 }}>
                    <Typography variant="h4" gutterBottom>ðŸŽ‰ Quiz Completed!</Typography>
                    <Typography variant="h5">Score: {(quizResult.score * 100).toFixed(0)}%</Typography>
                    <Typography variant="body1" sx={{ mt: 1 }}>
                      Correct: {quizResult.correct} / {quizResult.total}
                    </Typography>
                    <Typography variant="h6" sx={{ mt: 2 }}>
                      Skill Level: {quizResult.inferredSkill}
                    </Typography>
                  </CardContent>
                </Card>

                <Alert severity="success" sx={{ mb: 3 }}>
                  Your personalized learning path has been generated and saved!
                </Alert>

                <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
                  <Button variant="contained" size="large" onClick={() => setCurrentTab(0)}>
                    Go to Dashboard
                  </Button>
                  <Button variant="outlined" size="large" onClick={() => setCurrentTab(2)}>
                    View Learning Path
                  </Button>
                </Box>
              </Box>
            )}
          </Paper>
        )}

        {/* TAB 2: LEARNING PATH */}
        {currentTab === 2 && (
          <Paper sx={{ p: 4 }}>
            <Typography variant="h4" gutterBottom>ðŸ“š Your Learning Path</Typography>
            {!learningPath ? (
              <Box sx={{ textAlign: 'center', py: 6 }}>
                <Timeline sx={{ fontSize: 80, color: 'primary.main', mb: 2 }} />
                <Typography variant="h5" gutterBottom>No Learning Path Yet</Typography>
                <Typography variant="body1" color="textSecondary" paragraph>
                  Take a quiz or generate a learning path to get started
                </Typography>
                <Box sx={{ mt: 3, display: 'flex', gap: 2, justifyContent: 'center' }}>
                  <Button variant="contained" size="large" onClick={() => setCurrentTab(1)}>
                    Take Quiz
                  </Button>
                  <Button variant="outlined" size="large" onClick={() => setCurrentTab(0)}>
                    Go to Dashboard
                  </Button>
                </Box>
              </Box>
            ) : (
              <Box>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                  <Typography variant="h5">{learningPath.title || 'Your Learning Path'}</Typography>
                  <Button variant="contained" color="success" startIcon={<GetApp />} onClick={exportToCSV}>
                    Export CSV
                  </Button>
                </Box>

                <Grid container spacing={2} sx={{ mb: 4 }}>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography color="textSecondary" gutterBottom>Skill Level</Typography>
                        <Typography variant="h6">{learningPath.skillLevel || 'N/A'}</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography color="textSecondary" gutterBottom>Weekly Hours</Typography>
                        <Typography variant="h6">{learningPath.weeklyHours || 0}</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography color="textSecondary" gutterBottom>Estimated Weeks</Typography>
                        <Typography variant="h6">{learningPath.estimatedWeeks || 'N/A'}</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                  <Grid item xs={12} sm={6} md={3}>
                    <Card variant="outlined">
                      <CardContent>
                        <Typography color="textSecondary" gutterBottom>Total Hours</Typography>
                        <Typography variant="h6">{learningPath.totalHours || 0}</Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>

                <Typography variant="h5" gutterBottom>Learning Modules</Typography>
                <Divider sx={{ mb: 3 }} />

                {learningPath.modules?.map((module, idx) => (
                  <Card key={idx} sx={{ mb: 2 }}>
                    <CardContent>
                      <Typography variant="h6" color="primary.main">
                        {idx + 1}. {module.title}
                      </Typography>
                      <Typography variant="body2" color="textSecondary" paragraph>
                        {module.description}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        â±ï¸ {module.hours} hours
                      </Typography>
                      {module.resources && module.resources.length > 0 && (
                        <Box sx={{ mt: 2 }}>
                          <Typography variant="subtitle2" gutterBottom>Resources:</Typography>
                          {module.resources.map((res, rIdx) => (
                            <Chip key={rIdx} label={res} size="small" sx={{ mr: 1, mb: 1 }} />
                          ))}
                        </Box>
                      )}
                    </CardContent>
                  </Card>
                ))}

                <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
                  <Button variant="outlined" onClick={() => setCurrentTab(0)}>Back to Dashboard</Button>
                  <Button variant="contained" color="success" startIcon={<GetApp />} onClick={exportToCSV}>
                    Export to CSV
                  </Button>
                </Box>
              </Box>
            )}
          </Paper>
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
