import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
    Container,
    Paper,
    Typography,
    TextField,
    Button,
    Box,
    Alert,
    AppBar,
    Toolbar,
    Card,
    CardContent,
    Grid,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    Chip,
    Divider,
    LinearProgress,
    Tabs,
    Tab,
} from '@mui/material';
import {
    School,
    Quiz as QuizIcon,
    TrendingUp,
    GetApp,
    CheckCircle,
    Dashboard as DashboardIcon,
    Timeline,
    Refresh,
    Edit,
    PlayArrow,
} from '@mui/icons-material';
import axios from 'axios';

const theme = createTheme({
    palette: {
        primary: { main: '#2196f3' },
        secondary: { main: '#f50057' },
        success: { main: '#4caf50' },
    },
});

function App() {
    const [authPage, setAuthPage] = useState('login'); // 'login' or 'register'
    const [mainTab, setMainTab] = useState(0); // 0=Dashboard, 1=Quiz, 2=Learning Path
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);
    const [learningPath, setLearningPath] = useState(null);
    const [progress, setProgress] = useState({
        completedModules: [],
        currentModule: 0,
        overallProgress: 0,
    });

    // Quiz state
    const [topics, setTopics] = useState([]);
    const [selectedTopic, setSelectedTopic] = useState(null);
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const [quizResult, setQuizResult] = useState(null);
    const [quizForm, setQuizForm] = useState({
        name: localStorage.getItem('username') || '',
        target: '',
        weeklyHours: 10
    });

    const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

    // Load user data when logged in
    useEffect(() => {
        if (token) {
            loadUserData();
        }
    }, [token]);

    // Load quiz topics when quiz tab is active
    useEffect(() => {
        if (token && mainTab === 1 && topics.length === 0) {
            fetchTopics();
        }
    }, [mainTab, token]);

    const loadUserData = async () => {
        const storedUsername = localStorage.getItem('username');
        if (!storedUsername) return;

        try {
            // Try to load existing learning path
            const pathResponse = await axios.get(
                `${API_URL}/api/learning-path/user/${storedUsername}`,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            if (pathResponse.data) {
                setLearningPath(pathResponse.data);

                // Try to load progress
                try {
                    const progressResponse = await axios.get(
                        `${API_URL}/api/progress/user/${storedUsername}`,
                        { headers: { Authorization: `Bearer ${token}` } }
                    );
                    if (progressResponse.data) {
                        setProgress(progressResponse.data);
                    }
                } catch (err) {
                    console.log('No progress found, initializing...');
                    setProgress({
                        completedModules: [],
                        currentModule: 0,
                        overallProgress: 0,
                    });
                }
            }
        } catch (err) {
            console.log('No existing learning path found');
            // User hasn't created a learning path yet
        }
    };

    const fetchTopics = async () => {
        try {
            const response = await axios.get(`${API_URL}/api/quiz/topics`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setTopics(Array.from(response.data));
        } catch (err) {
            console.error('Error fetching topics:', err);
            setError('Failed to fetch quiz topics');
        }
    };

    const loadQuiz = async (topicId) => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get(`${API_URL}/api/quiz/${topicId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setQuiz(response.data);
            setSelectedTopic(topicId);
            setAnswers({});
            setQuizResult(null);
        } catch (err) {
            setError('Failed to load quiz');
        } finally {
            setLoading(false);
        }
    };

    const handleAnswerChange = (questionId, answerIndex) => {
        setAnswers({ ...answers, [questionId]: answerIndex });
    };

    const submitQuiz = async () => {
        if (Object.keys(answers).length < quiz.questions.length) {
            setError('Please answer all questions');
            return;
        }

        setLoading(true);
        setError('');
        try {
            const payload = {
                answers,
                weeklyHours: parseInt(quizForm.weeklyHours),
                name: quizForm.name,
                target: quizForm.target
            };

            const response = await axios.post(
                `${API_URL}/api/quiz/${selectedTopic}/submit`,
                payload,
                { headers: { Authorization: `Bearer ${token}` } }
            );

            setQuizResult(response.data);
            setLearningPath(response.data.learningPath);

            // Initialize progress
            const totalModules = response.data.learningPath.modules?.length || 0;
            setProgress({
                completedModules: [],
                currentModule: 0,
                overallProgress: 0,
                totalModules: totalModules
            });

            setSuccess('Quiz completed! Your learning path has been generated.');

            // Switch to dashboard after a moment
            setTimeout(() => {
                setMainTab(0);
                setSuccess('');
            }, 2000);

        } catch (err) {
            setError('Failed to submit quiz: ' + (err.response?.data?.message || err.message));
        } finally {
            setLoading(false);
        }
    };

    const markModuleComplete = (moduleIndex) => {
        if (progress.completedModules.includes(moduleIndex)) return;

        const updatedCompletedModules = [...progress.completedModules, moduleIndex];
        const totalModules = learningPath.modules?.length || 1;
        const newProgress = {
            ...progress,
            completedModules: updatedCompletedModules,
            currentModule: moduleIndex + 1,
            overallProgress: (updatedCompletedModules.length / totalModules) * 100
        };

        setProgress(newProgress);
        setSuccess(`Module ${moduleIndex + 1} marked as complete!`);
        setTimeout(() => setSuccess(''), 3000);
    };

    const resetProgress = () => {
        if (window.confirm('Are you sure you want to reset your progress?')) {
            setProgress({
                completedModules: [],
                currentModule: 0,
                overallProgress: 0,
                totalModules: learningPath.modules?.length || 0
            });
            setSuccess('Progress reset successfully!');
            setTimeout(() => setSuccess(''), 3000);
        }
    };

    const retakeQuiz = () => {
        if (window.confirm('Retaking the quiz will generate a new learning path. Continue?')) {
            setQuiz(null);
            setQuizResult(null);
            setAnswers({});
            setSelectedTopic(null);
            setMainTab(1); // Switch to quiz tab
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const response = await axios.post(`${API_URL}/api/auth/login`, {
                username,
                password,
            });

            setToken(response.data.token);
            localStorage.setItem('token', response.data.token);
            localStorage.setItem('username', username);

            // Load user data after login
            setTimeout(() => loadUserData(), 500);

        } catch (err) {
            setError('Login failed. Please check your credentials.');
        } finally {
            setLoading(false);
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            await axios.post(`${API_URL}/api/auth/register`, {
                username,
                password,
            });

            setSuccess('Registration successful! Please login.');
            setAuthPage('login');
            setPassword('');
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            setError('Registration failed. Username may already exist.');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        setToken(null);
        localStorage.clear();
        setAuthPage('login');
        setLearningPath(null);
        setQuiz(null);
        setQuizResult(null);
        setProgress({ completedModules: [], currentModule: 0, overallProgress: 0 });
        setMainTab(0);
        setTopics([]);
    };

    const generatePath = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.post(
                `${API_URL}/api/learning-path/generate`,
                {
                    name: localStorage.getItem('username'),
                    skillLevel: 'INTERMEDIATE',
                    interests: ['java', 'spring', 'docker'],
                    target: 'Backend Developer',
                    weeklyHours: 15,
                },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            setLearningPath(response.data);
            setProgress({
                completedModules: [],
                currentModule: 0,
                overallProgress: 0,
                totalModules: response.data.modules?.length || 0
            });
            setSuccess('Learning path generated successfully!');
            setTimeout(() => setSuccess(''), 3000);

        } catch (err) {
            setError('Failed to generate learning path');
        } finally {
            setLoading(false);
        }
    };

    const exportToCSV = async () => {
        if (!learningPath) return;

        try {
            const response = await axios.post(
                `${API_URL}/api/export/learning-path/csv`,
                learningPath,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    },
                    responseType: 'blob'
                }
            );

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            const filename = `learning-path-${localStorage.getItem('username')}-${Date.now()}.csv`;
            link.setAttribute('download', filename);
            document.body.appendChild(link);
            link.click();
            link.remove();
            window.URL.revokeObjectURL(url);

            setSuccess('Learning path exported successfully!');
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            setError('Failed to export CSV');
        }
    };

    // ============================================
    // LOGIN/REGISTER PAGE
    // ============================================
    if (!token) {
        return (
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Container maxWidth="sm" sx={{ mt: 8 }}>
                    <Paper elevation={3} sx={{ p: 4 }}>
                        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                            <School sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                            <Typography variant="h4" gutterBottom>
                                Learning Path Generator
                            </Typography>
                            <Typography variant="body2" color="textSecondary" sx={{ mb: 2 }}>
                                AI-Powered Personalized Learning
                            </Typography>

                            {error && <Alert severity="error" sx={{ width: '100%', mt: 2 }}>{error}</Alert>}
                            {success && <Alert severity="success" sx={{ width: '100%', mt: 2 }}>{success}</Alert>}

                            <Box component="form" onSubmit={authPage === 'login' ? handleLogin : handleRegister} sx={{ mt: 3, width: '100%' }}>
                                <TextField
                                    fullWidth
                                    label="Username"
                                    variant="outlined"
                                    margin="normal"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                                <TextField
                                    fullWidth
                                    label="Password"
                                    type="password"
                                    variant="outlined"
                                    margin="normal"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    size="large"
                                    disabled={loading}
                                    sx={{ mt: 3, mb: 2 }}
                                >
                                    {loading ? 'Please wait...' : authPage === 'login' ? 'SIGN IN' : 'REGISTER'}
                                </Button>

                                <Button
                                    fullWidth
                                    onClick={() => {
                                        setAuthPage(authPage === 'login' ? 'register' : 'login');
                                        setError('');
                                        setSuccess('');
                                    }}
                                >
                                    {authPage === 'login' ? 'Need an account? Register' : 'Have an account? Login'}
                                </Button>
                            </Box>
                        </Box>
                    </Paper>
                </Container>
            </ThemeProvider>
        );
    }

    // ============================================
    // MAIN APPLICATION
    // ============================================
    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />

            {/* App Bar */}
            <AppBar position="static">
                <Toolbar>
                    <School sx={{ mr: 2 }} />
                    <Typography variant="h6" sx={{ flexGrow: 1 }}>
                        Learning Path Generator
                    </Typography>
                    <Typography variant="body1" sx={{ mx: 2 }}>
                        Welcome, {localStorage.getItem('username')}!
                    </Typography>
                    <Button color="inherit" onClick={handleLogout}>
                        LOGOUT
                    </Button>
                </Toolbar>
            </AppBar>

            {/* Tab Navigation */}
            <Box sx={{ borderBottom: 1, borderColor: 'divider', bgcolor: 'background.paper' }}>
                <Tabs
                    value={mainTab}
                    onChange={(e, newValue) => {
                        setMainTab(newValue);
                        setError('');
                        setSuccess('');
                    }}
                    centered
                >
                    <Tab icon={<DashboardIcon />} label="Dashboard" />
                    <Tab icon={<QuizIcon />} label="Take Quiz" />
                    <Tab icon={<Timeline />} label="Learning Path" />
                </Tabs>
            </Box>

            <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                {error && <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>{error}</Alert>}
                {success && <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>{success}</Alert>}

                {/* ============================================ */}
                {/* TAB 0: DASHBOARD */}
                {/* ============================================ */}
                {mainTab === 0 && (
                    <>
                        {!learningPath ? (
                            // No learning path yet - show welcome
                            <Paper sx={{ p: 4, textAlign: 'center' }}>
                                <School sx={{ fontSize: 80, color: 'primary.main', mb: 2 }} />
                                <Typography variant="h4" gutterBottom>
                                    Welcome to Your Learning Journey!
                                </Typography>
                                <Typography variant="body1" color="textSecondary" paragraph>
                                    Get started by taking a quiz or generating a learning path
                                </Typography>

                                <Grid container spacing={3} sx={{ mt: 4 }}>
                                    <Grid item xs={12} md={6}>
                                        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', p: 3 }}>
                                            <Box sx={{ textAlign: 'center', flex: 1 }}>
                                                <QuizIcon sx={{ fontSize: 60, color: 'primary.main', mb: 2 }} />
                                                <Typography variant="h5" gutterBottom>
                                                    Take Assessment Quiz
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" paragraph>
                                                    Test your knowledge and get a personalized learning path based on your skill level
                                                </Typography>
                                            </Box>
                                            <Button
                                                variant="contained"
                                                size="large"
                                                startIcon={<PlayArrow />}
                                                onClick={() => setMainTab(1)}
                                                fullWidth
                                            >
                                                Start Quiz
                                            </Button>
                                        </Card>
                                    </Grid>

                                    <Grid item xs={12} md={6}>
                                        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', p: 3 }}>
                                            <Box sx={{ textAlign: 'center', flex: 1 }}>
                                                <TrendingUp sx={{ fontSize: 60, color: 'success.main', mb: 2 }} />
                                                <Typography variant="h5" gutterBottom>
                                                    Quick Generate
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary" paragraph>
                                                    Generate a learning path with default intermediate level settings
                                                </Typography>
                                            </Box>
                                            <Button
                                                variant="contained"
                                                size="large"
                                                color="success"
                                                onClick={generatePath}
                                                disabled={loading}
                                                fullWidth
                                            >
                                                {loading ? 'Generating...' : 'Generate Path'}
                                            </Button>
                                        </Card>
                                    </Grid>
                                </Grid>
                            </Paper>
                        ) : (
                            // Has learning path - show progress dashboard
                            <Box>
                                <Paper sx={{ p: 3, mb: 3 }}>
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                                        <Box>
                                            <Typography variant="h4" gutterBottom>
                                                 Your Progress Dashboard
                                            </Typography>
                                            <Typography variant="body2" color="textSecondary">
                                                Track your learning journey
                                            </Typography>
                                        </Box>
                                        <Box sx={{ display: 'flex', gap: 2 }}>
                                            <Button
                                                variant="outlined"
                                                startIcon={<Refresh />}
                                                onClick={resetProgress}
                                                size="small"
                                            >
                                                Reset
                                            </Button>
                                            <Button
                                                variant="outlined"
                                                startIcon={<Edit />}
                                                onClick={retakeQuiz}
                                                size="small"
                                            >
                                                Retake Quiz
                                            </Button>
                                        </Box>
                                    </Box>

                                    {/* Progress Stats */}
                                    <Grid container spacing={3} sx={{ mb: 4 }}>
                                        <Grid item xs={12} sm={6} md={3}>
                                            <Card sx={{ bgcolor: 'primary.main', color: 'white' }}>
                                                <CardContent sx={{ textAlign: 'center' }}>
                                                    <Typography variant="h3" fontWeight="bold">
                                                        {progress.overallProgress.toFixed(0)}%
                                                    </Typography>
                                                    <Typography variant="body2">
                                                        Overall Progress
                                                    </Typography>
                                                </CardContent>
                                            </Card>
                                        </Grid>
                                        <Grid item xs={12} sm={6} md={3}>
                                            <Card variant="outlined">
                                                <CardContent sx={{ textAlign: 'center' }}>
                                                    <Typography variant="h3" fontWeight="bold" color="success.main">
                                                        {progress.completedModules.length}
                                                    </Typography>
                                                    <Typography variant="body2" color="textSecondary">
                                                        Completed
                                                    </Typography>
                                                </CardContent>
                                            </Card>
                                        </Grid>
                                        <Grid item xs={12} sm={6} md={3}>
                                            <Card variant="outlined">
                                                <CardContent sx={{ textAlign: 'center' }}>
                                                    <Typography variant="h3" fontWeight="bold" color="primary.main">
                                                        {learningPath.modules?.length || 0}
                                                    </Typography>
                                                    <Typography variant="body2" color="textSecondary">
                                                        Total Modules
                                                    </Typography>
                                                </CardContent>
                                            </Card>
                                        </Grid>
                                        <Grid item xs={12} sm={6} md={3}>
                                            <Card variant="outlined">
                                                <CardContent sx={{ textAlign: 'center' }}>
                                                    <Typography variant="h3" fontWeight="bold" color="secondary.main">
                                                        {learningPath.weeklyHours}h
                                                    </Typography>
                                                    <Typography variant="body2" color="textSecondary">
                                                        Per Week
                                                    </Typography>
                                                </CardContent>
                                            </Card>
                                        </Grid>
                                    </Grid>

                                    {/* Progress Bar */}
                                    <Box sx={{ mb: 4 }}>
                                        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                                            <Typography variant="body2" fontWeight="bold">
                                                Learning Progress
                                            </Typography>
                                            <Typography variant="body2" color="textSecondary">
                                                {progress.completedModules.length} / {learningPath.modules?.length || 0} modules
                                            </Typography>
                                        </Box>
                                        <LinearProgress
                                            variant="determinate"
                                            value={progress.overallProgress}
                                            sx={{ height: 10, borderRadius: 5 }}
                                        />
                                    </Box>

                                    {/* Module List */}
                                    <Typography variant="h5" gutterBottom sx={{ mt: 4 }}>
                                        Learning Modules
                                    </Typography>
                                    <Divider sx={{ mb: 3 }} />

                                    {learningPath.modules && learningPath.modules.map((module, index) => {
                                        const isCompleted = progress.completedModules.includes(index);
                                        const isCurrent = progress.currentModule === index && !isCompleted;

                                        return (
                                            <Card
                                                key={index}
                                                sx={{
                                                    mb: 2,
                                                    border: isCurrent ? 2 : 1,
                                                    borderColor: isCurrent ? 'primary.main' : isCompleted ? 'success.main' : 'divider',
                                                    bgcolor: isCompleted ? 'success.light' : 'background.paper',
                                                }}
                                            >
                                                <CardContent>
                                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                                                        <Box sx={{ flex: 1 }}>
                                                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                                                {isCompleted && <CheckCircle sx={{ color: 'success.main', mr: 1 }} />}
                                                                {isCurrent && <Chip label="Current" color="primary" size="small" sx={{ mr: 1 }} />}
                                                                <Typography variant="h6" color={isCompleted ? 'success.dark' : 'primary.main'}>
                                                                    {index + 1}. {module.title}
                                                                </Typography>
                                                            </Box>
                                                            <Typography variant="body2" color="textSecondary" paragraph>
                                                                {module.description}
                                                            </Typography>
                                                            <Typography variant="caption" color="textSecondary">
                                                                 {module.hours} hours
                                                            </Typography>
                                                            {module.resources && module.resources.length > 0 && (
                                                                <Box sx={{ mt: 2 }}>
                                                                    <Typography variant="subtitle2" gutterBottom>
                                                                        Resources:
                                                                    </Typography>
                                                                    {module.resources.map((resource, rIdx) => (
                                                                        <Chip
                                                                            key={rIdx}
                                                                            label={resource}
                                                                            size="small"
                                                                            sx={{ mr: 1, mb: 1 }}
                                                                        />
                                                                    ))}
                                                                </Box>
                                                            )}
                                                        </Box>
                                                        {!isCompleted && (
                                                            <Button
                                                                variant="contained"
                                                                color="success"
                                                                size="small"
                                                                startIcon={<CheckCircle />}
                                                                onClick={() => markModuleComplete(index)}
                                                            >
                                                                Complete
                                                            </Button>
                                                        )}
                                                    </Box>
                                                </CardContent>
                                            </Card>
                                        );
                                    })}

                                    <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
                                        <Button
                                            variant="outlined"
                                            onClick={() => setMainTab(2)}
                                        >
                                            View Full Learning Path
                                        </Button>
                                        <Button
                                            variant="contained"
                                            color="success"
                                            startIcon={<GetApp />}
                                            onClick={exportToCSV}
                                        >
                                            Export to CSV
                                        </Button>
                                    </Box>
                                </Paper>
                            </Box>
                        )}
                    </>
                )}

                {/* ============================================ */}
                {/* TAB 1: QUIZ */}
                {/* ============================================ */}
                {mainTab === 1 && (
                    <Paper sx={{ p: 4 }}>
                        <Typography variant="h4" gutterBottom>
                             Skill Assessment Quiz
                        </Typography>
                        <Typography variant="body1" color="textSecondary" paragraph>
                            Test your knowledge and get a personalized learning path
                        </Typography>

                        {!quiz && !quizResult && (
                            <Box sx={{ mt: 4 }}>
                                <Typography variant="h5" gutterBottom>
                                    Select a Quiz Topic
                                </Typography>
                                {topics.length === 0 ? (
                                    <Box sx={{ textAlign: 'center', py: 4 }}>
                                        <Typography color="textSecondary">Loading quiz topics...</Typography>
                                    </Box>
                                ) : (
                                    <Grid container spacing={2} sx={{ mt: 2 }}>
                                        {topics.map(topicId => (
                                            <Grid item xs={12} sm={6} md={4} key={topicId}>
                                                <Card
                                                    sx={{
                                                        cursor: 'pointer',
                                                        transition: 'all 0.3s',
                                                        '&:hover': {
                                                            transform: 'translateY(-5px)',
                                                            boxShadow: 4
                                                        }
                                                    }}
                                                    onClick={() => loadQuiz(topicId)}
                                                >
                                                    <CardContent sx={{ textAlign: 'center', py: 4 }}>
                                                        <QuizIcon sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                                                        <Typography variant="h6">
                                                            {topicId.replace('t-', '').replace(/-/g, ' ').toUpperCase()}
                                                        </Typography>
                                                    </CardContent>
                                                </Card>
                                            </Grid>
                                        ))}
                                    </Grid>
                                )}
                            </Box>
                        )}

                        {quiz && !quizResult && (
                            <Box sx={{ mt: 4 }}>
                                <Typography variant="h5" gutterBottom>
                                    {quiz.topicName}
                                </Typography>

                                <Box sx={{ mb: 4, p: 3, bgcolor: 'background.default', borderRadius: 2 }}>
                                    <Typography variant="h6" gutterBottom>
                                        Your Information
                                    </Typography>
                                    <Grid container spacing={2}>
                                        <Grid item xs={12} md={4}>
                                            <TextField
                                                fullWidth
                                                label="Your Name"
                                                value={quizForm.name}
                                                onChange={(e) => setQuizForm({...quizForm, name: e.target.value})}
                                            />
                                        </Grid>
                                        <Grid item xs={12} md={4}>
                                            <TextField
                                                fullWidth
                                                label="Career Target"
                                                placeholder="e.g., Backend Developer"
                                                value={quizForm.target}
                                                onChange={(e) => setQuizForm({...quizForm, target: e.target.value})}
                                            />
                                        </Grid>
                                        <Grid item xs={12} md={4}>
                                            <TextField
                                                fullWidth
                                                type="number"
                                                label="Weekly Hours"
                                                value={quizForm.weeklyHours}
                                                onChange={(e) => setQuizForm({...quizForm, weeklyHours: e.target.value})}
                                                inputProps={{ min: 1, max: 40 }}
                                            />
                                        </Grid>
                                    </Grid>
                                </Box>

                                {quiz.questions.map((question, idx) => (
                                    <Card key={question.id} sx={{ mb: 3 }}>
                                        <CardContent>
                                            <Typography variant="h6" gutterBottom>
                                                Question {idx + 1}: {question.text}
                                            </Typography>
                                            <FormControl component="fieldset" fullWidth>
                                                <RadioGroup
                                                    value={answers[question.id] !== undefined ? answers[question.id] : ''}
                                                    onChange={(e) => handleAnswerChange(question.id, parseInt(e.target.value))}
                                                >
                                                    {question.options.map((option, optIdx) => (
                                                        <FormControlLabel
                                                            key={optIdx}
                                                            value={optIdx}
                                                            control={<Radio />}
                                                            label={option}
                                                            sx={{
                                                                p: 1,
                                                                m: 0.5,
                                                                border: 1,
                                                                borderColor: 'divider',
                                                                borderRadius: 1,
                                                                '&:hover': { bgcolor: 'action.hover' }
                                                            }}
                                                        />
                                                    ))}
                                                </RadioGroup>
                                            </FormControl>
                                        </CardContent>
                                    </Card>
                                ))}

                                <Box sx={{ display: 'flex', gap: 2, mt: 3 }}>
                                    <Button
                                        variant="outlined"
                                        onClick={() => {
                                            setQuiz(null);
                                            setAnswers({});
                                            setSelectedTopic(null);
                                        }}
                                    >
                                        Back to Topics
                                    </Button>
                                    <Button
                                        variant="contained"
                                        size="large"
                                        onClick={submitQuiz}
                                        disabled={loading || Object.keys(answers).length < quiz.questions.length}
                                    >
                                        {loading ? 'Submitting...' : `Submit Quiz (${Object.keys(answers).length}/${quiz.questions.length})`}
                                    </Button>
                                </Box>
                            </Box>
                        )}

                        {quizResult && (
                            <Box sx={{ mt: 4 }}>
                                <Card sx={{ mb: 3, bgcolor: 'success.main', color: 'white' }}>
                                    <CardContent sx={{ textAlign: 'center', py: 4 }}>
                                        <Typography variant="h4" gutterBottom>
                                             Quiz Completed!
                                        </Typography>
                                        <Typography variant="h5">
                                            Score: {(quizResult.score * 100).toFixed(0)}%
                                        </Typography>
                                        <Typography variant="body1" sx={{ mt: 1 }}>
                                            Correct: {quizResult.correct} / {quizResult.total}
                                        </Typography>
                                        <Typography variant="h6" sx={{ mt: 2 }}>
                                            Skill Level: {quizResult.inferredSkill}
                                        </Typography>
                                    </CardContent>
                                </Card>

                                <Alert severity="success" sx={{ mb: 3 }}>
                                    Your personalized learning path has been generated! Check your dashboard to start learning.
                                </Alert>

                                <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
                                    <Button
                                        variant="contained"
                                        size="large"
                                        onClick={() => setMainTab(0)}
                                    >
                                        Go to Dashboard
                                    </Button>
                                    <Button
                                        variant="outlined"
                                        size="large"
                                        onClick={() => setMainTab(2)}
                                    >
                                        View Learning Path
                                    </Button>
                                </Box>
                            </Box>
                        )}
                    </Paper>
                )}

                {/* ============================================ */}
                {/* TAB 2: LEARNING PATH */}
                {/* ============================================ */}
                {mainTab === 2 && (
                    <>
                        {!learningPath ? (
                            <Paper sx={{ p: 4, textAlign: 'center' }}>
                                <Timeline sx={{ fontSize: 60, color: 'primary.main', mb: 2 }} />
                                <Typography variant="h5" gutterBottom>
                                    No Learning Path Yet
                                </Typography>
                                <Typography color="textSecondary" paragraph>
                                    Take a quiz or generate a learning path to get started
                                </Typography>
                                <Box sx={{ mt: 3, display: 'flex', gap: 2, justifyContent: 'center' }}>
                                    <Button
                                        variant="contained"
                                        onClick={() => setMainTab(1)}
                                    >
                                        Take Quiz
                                    </Button>
                                    <Button
                                        variant="outlined"
                                        onClick={() => setMainTab(0)}
                                    >
                                        Go to Dashboard
                                    </Button>
                                </Box>
                            </Paper>
                        ) : (
                            <Paper sx={{ p: 3 }}>
                                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                                    <Typography variant="h4">
                                        {learningPath.title}
                                    </Typography>
                                    <Button
                                        variant="contained"
                                        color="success"
                                        startIcon={<GetApp />}
                                        onClick={exportToCSV}
                                    >
                                        Export CSV
                                    </Button>
                                </Box>

                                {/* Path Info */}
                                <Grid container spacing={2} sx={{ mb: 4 }}>
                                    <Grid item xs={12} sm={6} md={3}>
                                        <Card variant="outlined">
                                            <CardContent>
                                                <Typography color="textSecondary" gutterBottom>
                                                    Skill Level
                                                </Typography>
                                                <Typography variant="h6">
                                                    {learningPath.skillLevel}
                                                </Typography>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                    <Grid item xs={12} sm={6} md={3}>
                                        <Card variant="outlined">
                                            <CardContent>
                                                <Typography color="textSecondary" gutterBottom>
                                                    Weekly Hours
                                                </Typography>
                                                <Typography variant="h6">
                                                    {learningPath.weeklyHours}
                                                </Typography>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                    <Grid item xs={12} sm={6} md={3}>
                                        <Card variant="outlined">
                                            <CardContent>
                                                <Typography color="textSecondary" gutterBottom>
                                                    Estimated Weeks
                                                </Typography>
                                                <Typography variant="h6">
                                                    {learningPath.estimatedWeeks}
                                                </Typography>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                    <Grid item xs={12} sm={6} md={3}>
                                        <Card variant="outlined">
                                            <CardContent>
                                                <Typography color="textSecondary" gutterBottom>
                                                    Total Hours
                                                </Typography>
                                                <Typography variant="h6">
                                                    {learningPath.totalHours}
                                                </Typography>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                </Grid>

                                <Typography variant="h5" gutterBottom>
                                    Learning Modules
                                </Typography>
                                <Divider sx={{ mb: 3 }} />

                                {learningPath.modules && learningPath.modules.map((module, index) => (
                                    <Card key={index} sx={{ mb: 2 }}>
                                        <CardContent>
                                            <Typography variant="h6" color="primary.main">
                                                {index + 1}. {module.title}
                                            </Typography>
                                            <Typography variant="body2" color="textSecondary" paragraph>
                                                {module.description}
                                            </Typography>
                                            <Typography variant="caption">
                                                 {module.hours} hours
                                            </Typography>
                                            {module.resources && module.resources.length > 0 && (
                                                <Box sx={{ mt: 2 }}>
                                                    <Typography variant="subtitle2" gutterBottom>
                                                        Resources:
                                                    </Typography>
                                                    {module.resources.map((resource, rIdx) => (
                                                        <Chip
                                                            key={rIdx}
                                                            label={resource}
                                                            size="small"
                                                            sx={{ mr: 1, mb: 1 }}
                                                        />
                                                    ))}
                                                </Box>
                                            )}
                                        </CardContent>
                                    </Card>
                                ))}

                                <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
                                    <Button
                                        variant="outlined"
                                        onClick={() => setMainTab(0)}
                                    >
                                        Back to Dashboard
                                    </Button>
                                    <Button
                                        variant="contained"
                                        color="success"
                                        startIcon={<GetApp />}
                                        onClick={exportToCSV}
                                    >
                                        Export to CSV
                                    </Button>
                                </Box>
                            </Paper>
                        )}
                    </>
                )}
            </Container>
        </ThemeProvider>
    );
}

export default App;
