import React, { useState, useCallback, useEffect } from 'react';
import axios from 'axios';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';
import AdminPanel from './components/AdminPanel';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(null);
  const [role, setRole] = useState(null);
  const [view, setView] = useState('login');
  const [learningPath, setLearningPath] = useState(null);
  const [progress, setProgress] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Initial load - check if user is already logged in
  useEffect(() => {
    const storedUser = localStorage.getItem('currentUser');
    const storedToken = localStorage.getItem('token');
    const storedRole = localStorage.getItem('role');

    console.log('Initial load check:', { storedUser, storedToken, storedRole });

    if (storedUser && storedToken && storedRole) {
      setCurrentUser(storedUser);
      setToken(storedToken);
      setRole(storedRole);
      setView('dashboard'); // Always start at dashboard when logged in
      loadUserData(storedUser, storedToken);
    } else {
      setView('login'); // Ensure we start at login if not authenticated
    }

    setIsLoading(false);
  }, []);

  const loadUserData = useCallback(async (username, token) => {
    if (!username || !token) return;

    try {
      const resp = await axios.get(
        `${API_URL}/api/progress/user/${username}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (resp.data) {
        setProgress(resp.data);
        localStorage.setItem(`progress_${username}`, JSON.stringify(resp.data));
      }
    } catch (err) {
      if (err.response?.status === 404) {
        console.log('No progress found for user (they haven\'t taken a quiz yet)');
        setProgress(null);
      } else {
        console.error('Error loading progress:', err);
      }

      const storedProgress = localStorage.getItem(`progress_${username}`);
      if (storedProgress) {
        try {
          setProgress(JSON.parse(storedProgress));
        } catch (e) {
          console.error('Error parsing stored progress:', e);
        }
      }
    }

    const storedPath = localStorage.getItem(`learningPath_${username}`);
    if (storedPath) {
      try {
        setLearningPath(JSON.parse(storedPath));
      } catch (e) {
        console.error('Error parsing stored learning path:', e);
      }
    }
  }, []);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/auth/login`, { 
        username, 
        password 
      });

      console.log('Login response:', response.data);

      const userRole = response.data.role || 'USER';

      setCurrentUser(username);
      setToken(response.data.token);
      setRole(userRole);
      setView('dashboard'); // Always go to dashboard after login

      localStorage.setItem('currentUser', username);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', userRole);

      console.log('User logged in:', { username, role: userRole });

      await loadUserData(username, response.data.token);

      return { success: true };
    } catch (error) {
      console.error('Login error:', error);
      return { 
        success: false, 
        message: error.response?.data?.message || error.response?.data || 'Login failed' 
      };
    }
  };

  const handleRegister = async (username, password, registerAsAdmin = false) => {
    try {
      await axios.post(`${API_URL}/api/auth/register`, { 
        username, 
        password, 
        role: registerAsAdmin ? "ADMIN" : "USER" 
      });
      return { success: true };
    } catch (error) {
      console.error('Registration error:', error);
      return { 
        success: false, 
        message: typeof error.response?.data === 'string' 
          ? error.response.data 
          : 'Registration failed' 
      };
    }
  };

  const handleLogout = () => {
    console.log('Logging out...');

    // Clear all stored data
    const userToLogout = currentUser;

    localStorage.removeItem('currentUser');
    localStorage.removeItem('token');
    localStorage.removeItem('role');

    if (userToLogout) {
      localStorage.removeItem(`progress_${userToLogout}`);
      localStorage.removeItem(`learningPath_${userToLogout}`);
    }

    // Reset all state
    setCurrentUser(null);
    setToken(null);
    setRole(null);
    setLearningPath(null);
    setProgress(null);
    setView('login'); // Always go back to login

    console.log('Logged out successfully');
  };

  const handlePathGenerated = (newPath, newProgress) => {
    setLearningPath(newPath);
    setProgress(newProgress);

    if (currentUser) {
      if (newPath) {
        localStorage.setItem(`learningPath_${currentUser}`, JSON.stringify(newPath));
      }
      if (newProgress) {
        localStorage.setItem(`progress_${currentUser}`, JSON.stringify(newProgress));
      }
    }

    setView('dashboard');
  };

  const handleProgressUpdate = async (updatedProgress) => {
    try {
      const response = await axios.post(
        `${API_URL}/api/progress/update`,
        updatedProgress,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setProgress(response.data);

      if (currentUser) {
        localStorage.setItem(`progress_${currentUser}`, JSON.stringify(response.data));
      }

      return { success: true };
    } catch (error) {
      console.error('Progress update error:', error);
      return { 
        success: false, 
        message: error.response?.data?.error || 'Failed to update progress' 
      };
    }
  };

  if (isLoading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        fontSize: '1.5rem'
      }}>
        Loading...
      </div>
    );
  }

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <h1 className="app-title">üéì Learning Path Generator</h1>
          {currentUser && (
            <div className="header-right">
              <nav className="nav-menu" style={{ display: 'flex', gap: '1rem' }}>
                <button 
                  onClick={() => {
                    console.log('Dashboard clicked');
                    setView('dashboard');
                  }} 
                  className={view === 'dashboard' ? 'active' : ''}
                  style={{
                    background: view === 'dashboard' ? 'white' : 'rgba(255, 255, 255, 0.15)',
                    color: view === 'dashboard' ? '#667eea' : 'white',
                    border: '2px solid',
                    borderColor: view === 'dashboard' ? 'white' : 'rgba(255, 255, 255, 0.3)',
                    padding: '0.7rem 1.5rem',
                    borderRadius: '8px',
                    fontWeight: '600',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    transition: 'all 0.3s'
                  }}
                >
                  Dashboard
                </button>

                {role === 'ADMIN' && (
                  <button 
                    onClick={() => {
                      console.log('Admin Panel clicked, role:', role);
                      setView('admin');
                    }} 
                    className={view === 'admin' ? 'active' : ''}
                    style={{
                      background: view === 'admin' ? 'white' : 'rgba(255, 255, 255, 0.15)',
                      color: view === 'admin' ? '#667eea' : 'white',
                      border: '2px solid',
                      borderColor: view === 'admin' ? 'white' : 'rgba(255, 255, 255, 0.3)',
                      padding: '0.7rem 1.5rem',
                      borderRadius: '8px',
                      fontWeight: '600',
                      fontSize: '1rem',
                      cursor: 'pointer',
                      transition: 'all 0.3s'
                    }}
                  >
                    Admin Panel
                  </button>
                )}
              </nav>

              <div className="user-info" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                <span className="user-name" style={{ fontWeight: '600', color: 'white' }}>
                  üë§ {currentUser}
                </span>
                <span 
                  className={`user-role ${role === 'ADMIN' ? 'admin' : 'user'}`}
                  style={{
                    padding: '0.5rem 1rem',
                    borderRadius: '20px',
                    fontWeight: '700',
                    fontSize: '0.9rem',
                    background: role === 'ADMIN' ? '#fbbf24' : 'rgba(255, 255, 255, 0.2)',
                    color: role === 'ADMIN' ? '#92400e' : 'white',
                    boxShadow: role === 'ADMIN' ? '0 2px 8px rgba(251, 191, 36, 0.3)' : 'none'
                  }}
                >
                  {role === 'ADMIN' ? 'üõ°Ô∏è Admin' : 'üë• User'}
                </span>
                <button 
                  onClick={handleLogout} 
                  className="logout-btn"
                  style={{
                    background: 'rgba(255, 255, 255, 0.2)',
                    color: 'white',
                    border: '2px solid rgba(255, 255, 255, 0.3)',
                    padding: '0.7rem 1.5rem',
                    borderRadius: '8px',
                    fontWeight: '600',
                    cursor: 'pointer',
                    transition: 'all 0.3s'
                  }}
                >
                  Logout
                </button>
              </div>
            </div>
          )}
        </div>
      </header>

      <main className="app-main">
        {view === 'login' && !currentUser && (
          <Login 
            onLogin={handleLogin} 
            onSwitchToRegister={() => setView('register')} 
          />
        )}

        {view === 'register' && !currentUser && (
          <Register 
            onRegister={handleRegister} 
            onSwitchToLogin={() => setView('login')} 
          />
        )}

        {currentUser && view === 'dashboard' && (
          <Dashboard
            learningPath={learningPath}
            progress={progress}
            role={role}
            onStartQuiz={() => setView('quiz')}
            onProgressUpdate={handleProgressUpdate}
          />
        )}

        {currentUser && view === 'quiz' && (
          <Quiz
            currentUser={currentUser}
            token={token}
            onBack={() => setView('dashboard')}
            onPathGenerated={handlePathGenerated}
          />
        )}

        {currentUser && view === 'admin' && role === 'ADMIN' && (
          <AdminPanel token={token} role={role} />
        )}

        {currentUser && view === 'admin' && role !== 'ADMIN' && (
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: '60vh',
            fontSize: '1.5rem',
            color: '#ef4444'
          }}>
            ‚õî Access Denied - Admin Only
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
